css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

#top iframe{display : none;}

#contenu table td,
#contenu table td div{
opacity : 1;
color : white !important;
background: rgba(0, 0, 0, 0.4) !important; 
border-radius : 5px;

}
#id_quote { border: solid 1px #fff;}

body {
background-image: url(http://marcdelage.unblog.fr/files/2010/10/coquelicot.jpg)!important;
background-color:#000000 !important;
background-attachment:fixed;
}

div#top {
background-image: url(http://marcdelage.unblog.fr/files/2010/10/coquelicot.jpg) !important;
}

.b1 { background-color:#444444!important; }
.b2,.b3,.tgg,.tggm,.tah,.tah th,h1,
.top_menu_droite,.top_menu_gauche,#conteneur{ border-color:#000000!important; }

.tggm,.tah .tggm {border-top: 1px rgba(0, 0, 0, 0) !important;}
.rr1 .bas .b2,.rr1 .bas .b3,
.rr2 .bas .b2,.rr2 .bas .b3,
.ras4 .bas .b2,.ras4 .bas .b3,
.ram4 .bas .b2,.ram4 .bas .b3,
.rsm2 .bas .b2,.rsm2 .bas .b3,
.rsm3 .bas .b2,.rsm3 .bas .b3,
.rmt .bas .b2,.rmt .bas .b3,
.titre_h1 .bas .b2,.titre_h1 .bas .b3 { background: rgba(0, 0, 0, 0.6) !important;}
.tgg,h1,th { background: rgba(0, 0, 0, 0.6) !important;}
.haut .b2,.haut .b3 { background: rgba(0, 0, 0, 0.6) !important;}
.tggm ,.bas .b2,.bas .b3,
.rsm1 .haut .b2,.rsm1 .haut .b3,
.rsm4 .haut .b2,.rsm4 .haut .b3,
.rf3 .haut .b2,.rf3 .haut .b3 { background: rgba(0, 0, 0, 0.6) !important;}
#conteneur,.conteneur .b2,.conteneur .b3 { background: rgba(0, 0, 0, 0.2) !important;}
.table_fond {background: rgba(0, 0, 0, 0.0) !important;}

th.top_menu{
border-right: 1px rgba(0, 0, 0, 0) !important;
border-left: 1px rgba(0, 0, 0, 0) !important;
}
th.top_menu_droite{
border-left: 1px rgba(0, 0, 0, 0) !important;
}
th.top_menu_gauche{
border-right: 1px rgba(0, 0, 0, 0) !important;
}
}
