css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("archiv.to") {

body{
  color: #eee !important;
  background: #0D0D0D url('http://farm5.static.flickr.com/4023/4559510781_eef2404c97.jpg') !important;
  background-repeat: repeat;
  background-attachment: fixed !important;
  background-position: center !important;
}
a{
  text-decoration: none !important;
  color: #eee !important;
  -webkit-transition: color 300ms ease-in-out;
  -moz-transition: color 300ms ease-in-out;
  -o-transition: color 300ms ease-in-out;
  transition: color 300ms ease-in-out;
}
a:hover{ color: orange !important;}
td a img { height:0 !important;}
b{ text-transform: uppercase;}
center{ height:0 !important;}

.button a{
  border:1px solid none !important;
  -moz-border-radius-topright: 20px 40px;
  -moz-border-radius-topleft: 20px 40px;
  -webkit-border-top-right-radius: 20px 40px;
  -webkit-border-top-left-radius: 20px 40px;
}
.edge-left { background-image: none !important;}
.edge-right { background-image: none !important;}

#header {
  border:1px solid none !important;
  -moz-border-radius-bottomright: 300px 50px;
  -moz-border-radius-bottomleft: 300px 50px;
  -webkit-border-bottom-right-radius: 300px 50px;
  -webkit-border-bottom-left-radius: 300px 50px;

  -webkit-box-shadow: 0px 0px 6px white;
  -moz-box-shadow: 0px 0px 6px white;
  -o-box-shadow: 0px 0px 6px white;
  -icab-box-shadow: 0px 0px 6px white;
  -khtml-box-shadow: 0px 0px 6px white;
  box-shadow: 0px 0px 6px white;
}

#go1 { display: none !important;}

map { height: 0px !important}

#go2 {
  display: block !important;

  background: #ccc;
  border:3px solid #014e60;
  -webkit-border-radius: 10px;
  -moz-border-radius:10px;
  -o-border-radius: 10px;
  -khtml-border-radius: 10px;
  border-radius: 10px;

  -webkit-box-shadow: 0px 0px 6px white;
  -moz-box-shadow: 0px 0px 6px white;
  -o-box-shadow: 0px 0px 6px white;
  -icab-box-shadow: 0px 0px 6px white;
  -khtml-box-shadow: 0px 0px 6px white;
  box-shadow: 0px 0px 6px white;

}

iframe {visibility: hidden;}
