.fields {
  padding-left: 12px !important;
  background: #fff;
  border:3px solid #014e60;
  -webkit-border-radius: 10px;
  -moz-border-radius:10px;
  -o-border-radius: 10px;
  -khtml-border-radius: 10px;
  border-radius: 10px;

  -webkit-box-shadow: 0px 0px 6px white;
  -moz-box-shadow: 0px 0px 6px white;
  -o-box-shadow: 0px 0px 6px white;
  -icab-box-shadow: 0px 0px 6px white;
  -khtml-box-shadow: 0px 0px 6px white;
  box-shadow: 0px 0px 6px white;
}
span{
  -webkit-transition: color 500ms linear;
  -moz-transition: color 500ms linear;
  -o-transition: color 500ms linear;
  transition: color 500ms linear;
}
span:hover{
  color: #000 !important;
}
.fields a{
  color: #000 !important;
  text-shadow: -1px -1px 1px #969696, 1px 1px 1px #fff;
  -webkit-transition: all 500ms ease-in-out;
  -moz-transition: all 500ms ease-in-out;
  -o-transition: all 500ms ease-in-out;
  transition: all 500ms ease-in-out;
}
.fields a:hover{
  color: #fff !important;
  text-shadow: 0px 0px 5px #000, 0px 0px 5px #000, 0px 0px 5px #000;
}
label{ color: #000 !important;}

.box_top{ height: 0px !important;}
.box_bottom{ height: 0px !important;}
.box_center a{
  font-weight:bold;
  color: #000 !important;
  -webkit-transition: color 500ms ease-in-out;
  -moz-transition: color 500ms ease-in-out;
  -o-transition: color 500ms ease-in-out;
  transition: color 500ms ease-in-out;
}
.box_center a:hover{
  font-weight:bold;
  color: orange !important;
}
.box{
  height: 52px !important;
  width: 1026px !important;
  background: #fff;
  border:3px solid #014e60;
  -webkit-border-radius: 10px;
  -moz-border-radius: 10px;
  -o-border-radius: 10px;
  -khtml-border-radius: 10px;
  border-radius: 10px;

  -webkit-box-shadow: 0px 0px 6px white;
  -moz-box-shadow: 0px 0px 6px white;
  -o-box-shadow: 0px 0px 6px white;
  -icab-box-shadow: 0px 0px 6px white;
  -khtml-box-shadow: 0px 0px 6px white;
  box-shadow: 0px 0px 6px white;
}

#footer {
  width: 1030px !important; 
  height: 37px !important; 

  border:1px solid none;
  -webkit-border-radius: 10px;
  -moz-border-radius:10px;
  -o-border-radius: 10px;
  -khtml-border-radius: 10px;
  border-radius: 10px;

  -webkit-box-shadow: inset 0px 0px 4px #969696;
  -moz-box-shadow: inset 0px 0px 4px #969696;
  -o-box-shadow: inset 0px 0px 4px #969696;
  box-shadow: inset 0px 0px 4px #969696; 	
}

#ball {display: none;}
}
