css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(entropiaforum.com) {

body {
    background-image: none !important;
    background-color: #FFFFFF !important;
}

.page, 
.alt1,
table, tr, td, tbody, th,
div div,
.alt2 div .smallfont a img {
background-color: #FFFFFF !important;
background-image: none !important;
}

.tcat, tcat a, .thead, .tborder {
    color: #000000 !important;
}

.alt1 td {
border-top: 1px black solid !important;
border-bottom: 1px black solid !important;
}

div div div div .page div table tbody tr td .smallfont a img {
   display: inline !important;
}

/* copied from inline style on EF */

a:link, body_alink
{
	color: #000077 !important;
	text-decoration: underline !important;
}
a:visited, body_avisited
{
	color: #000077 !important;
	text-decoration: underline !important;
}
a:hover, a:active, body_ahover
{
	color: #000077 !important;
	text-decoration: none !important;
}
.page,
.button,
.fjsel,
.vbmenu_hilite
{
	color: #000000 !important;
}

.vbmenu_control {
color: #000000 !important;
}

.vbmenu_control a:link, .vbmenu_control_alink
{
	color: #000000 !important;
	text-decoration: none !important;
}
.vbmenu_control a:visited, .vbmenu_control_avisited
{
	color: #000000 !important;
	text-decoration: none !important;
}
.vbmenu_control a:hover, .vbmenu_control a:active, .vbmenu_control_ahover
{
	color: #000000 !important;
	text-decoration: underline !important;
}

.vbmenu_popup
{
	background: #000000 !important;
	color: #FFFFFF !important;
	border: 1px solid #0B198C !important;
}
.vbmenu_hilite a:link, .vbmenu_hilite_alink
{
	color: #009900 !important;
	text-decoration: none !important;
}
.vbmenu_hilite a:visited, .vbmenu_hilite_avisited
{
	color: #009900 !important;
	text-decoration: none !important;
}
.vbmenu_hilite a:hover, .vbmenu_hilite a:active, .vbmenu_hilite_ahover
{
	color: #009900 !important;
	text-decoration: none !important;
}


.tcat a:link, .top_links a:link
{
	color: #000000 !important;
	text-decoration: none !important;
}
.tcat a:visited, .top_links a:visited
{
	color: #000000 !important;
	text-decoration: none !important;
}
.tcat a:hover, .tcat a:active, .top_links a:hover, .top_links a:active
{
	color: #000000 !important;
	text-decoration: underline !important;
}
}
