css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("phoronix.com") {

@font-face {
  font-family: 'Oswald';
  font-style: normal;
  font-weight: normal;
  src: local('Oswald '), local('Oswald'), url('http://themes.googleusercontent.com/font?kit=-g5pDUSRgvxvOl5u-a_WHw') format('woff');
}

body {
background-image: none !important; 
background-color: #DDD !important 
}

#phxcms_container { 
border: 0 !important; 
box-shadow: 0px 0px 5px #666 
}

#phxcms_content_left { 
margin-top: 27px !important; 
}

#phxcms_content_left li a:hover { 
background-color: #FFF !important; 
color: #2B6B29 !important; 
}

#phxcms_toplogo { 
/*background: none !important */
}

#phxcms_container div.phxcms_nav_int { 
display: none 
}

.phxcms_header_legacy { 
display: none; 
}

#phxcms_top_leaderboard { 
display: none !important; 
}

#phxcms_toplogo_top { 
display: none !important; 
}

#phxcms_toplogo {  }

#phxcms_content_phx h1 { 
font-family: 'Oswald';
color: #2B6B29
}

#phxcms_content_phx h2 { 
font-family: times; 
font-size: 10pt !important; 
color: #666; 
font-weight: normal 
}

#phxcms_content_left ol li { 
background-color: #FFF !important; 
margin-top: 0 !important; 
text-indent: 12px !important; 
color: #555 !important 
}

#phxcms_content_left ol li a { 
color: #666 !important 
}

#phxcms_content_center { 
border: 0 !important 
}

.phxcms_normal_format { 
font-size: 12pt; 
line-height: 1.5em !important; 
font-family: times;
color: #333
}

#phxcms_part_body { 
background-color: #FFF !important  
}

.phxcms_nav_header { 
background-color: #FFF !important; 
color: #444 !important; 
padding: 5px !important;
margin-top: 1.5em
}

#phxcms_right_menu { 
margin-top: 7px !important 
}

#phxcms_right_menu ol li {
background-color: #FFF !important;
margin-left: 5px !important
}

#phxcms_right_menu ol li a { 
color: #333 !important 
}

#phxcms_right_menu li a:hover { 
background-color: #FFF !important; 
color: #2B6B29 !important 
}

#phxcms_navigation_hover_box { 
padding: 10px 
}

.phxcms_navigation_format {
line-height: 1.5em !important;
font-size: 8pt
}

.phxcms_navigation_format a { font-size: 10pt; }

}
