css:
@-moz-document
	url("chrome://brief/content/brief.xul"),
	url("resource://brief-content/feedview-template.html"),
	url("resource://brief-content/feedview.html")
{

page
{
	background: rgb(0, 0, 0) !important;
}

/* */
#unread-folder, #starred-folder, #trash-folder,
.article-star, .bookmark-button,
.article-controls, .controls,
#filter-starred-checkbox
{
	visibility: collapse !important;
}

/* */
#feed-title,
#searchbar,
#searchbar .textbox-input
{
	cursor: default !important;
}

.textbox-search-icon,
.textbox-search-clear,
.brief-button
{
	cursor: pointer !important;
}

/* */
#update-progress-deck,
#update-progress
{
	margin: 0 !important;
	padding: 0 !important;
}

#update-progress
{
	margin: 2px 4px 2px 0 !important;
}

#update-progress,
#searchbar
{
	border-radius: 2px !important;
	background: 0 !important;
}

#sidebar-separator,
#feed-view-header
{
	border-bottom: 1px solid !important;
	margin: 0 !important;
	padding: 0 !important;
}

#update-progress,
#searchbar,
#sidebar-separator,
#feed-view-header
{
	border-color: rgb(255, 255, 255) !important;
}

#update-progress
{
	background:
		-moz-linear-gradient(
			rgb(0, 0, 0),
			rgb(128, 128, 128)
		) !important;
	border-color: rgb(255, 255, 255) !important;
	border-radius: 3px !important;
}
#update-progress .progress-bar
{
	margin: 1px !important;
	background:
		-moz-linear-gradient(
			rgb(91, 146, 213),
			rgb(47, 84, 104)
		) !important;
}

#sidebar
{
	margin: 0 !important;
	padding: 0 0 0 4px !important;
}

#reveal-sidebar-button,
#sidebar-splitter
{
	margin: 0 !important;
	padding: 0 !important;
	border: 0 !important;
	background: 0 !important;
}

#reveal-sidebar-button
{
	padding: 0 2px 0 2px !important;
	border-right: 1px solid rgb(255, 255, 255) !important;
}

#sidebar-splitter
{
	width: 4px !important;
	border-right: 1px solid rgb(255, 255, 255) !important;
}

/* */
.unread-count,
richlistitem label,
richtreefolder label,
.feed-title,
#feed-title,
#searchbar,
#main-message,
#secondary-message,
.day-header,
.headline-feed-name,
.feed-name,
.date
{
	font-family: Segoe UI !important;
	font-weight: 400 !important;
	color: rgb(255, 255, 255) !important;
}
.unread label
{
	font-weight: bold !important;
}
#feed-title
{
	font-size: 28px !important;
	text-decoration: none !important;
}

/* */
.unread-count,
richlistitem,
richlistitem label,
.folder-title-container,
richtreeitem,
richtreeitem label,
.feed-title
{
	height: 16px !important;
	font-size: 13px !important;
}

/* */
.folder-container
{
	margin: 0 !important;
	padding-top: 0 !important;
	padding-bottom: 0 !important;
}
richtreefolder
{
	background: 0 !important;
	padding: 0 !important;
	margin: 0 !important;
	border: 0 !important;
}

/* */
richlistitem,
.folder-title-container,
richtreeitem
{
	margin: 0 0 2px 0 !important;
	padding: 0 0 0 2px !important;
}
richlistitem
{
	margin: 2px 0 0 0 !important;
}
richlistitem:first-child
{
	margin: 0 !important;
}

/* */
.unread-count,
richlistitem,
.folder-title-container,
richtreeitem
{
	cursor: pointer !important;
	background: 0 !important;
	border-radius: 3px !important;
	border: 1px solid transparent !important;
}
.unread-count
{
	border-radius: 0px !important;
	border: 0 !important;
	margin: 0 !important;
}

/* Selected */
richlistitem:not(:hover)[selected=true],
.feed-folder[selected=true] > .folder-title-container:not(:hover),
richtreeitem:not(:hover)[selected=true]
{
	background: 
		-moz-linear-gradient(
			rgb(0, 0, 0),
			rgb(128, 128, 128)
		) !important;
}
richlistitem[selected=true],
.feed-folder[selected=true] > .folder-title-container,
richtreeitem[selected=true]
{
	border-color: rgb(255, 255, 255) !important;
}

/* Hover */
richlistitem:hover,
.folder-title-container:hover,
richtreeitem:hover
{
	background: 
		-moz-linear-gradient(
			rgb(0, 0, 0),
			rgb(47, 84, 104)
		) !important;
}
#reveal-sidebar-button:hover,
#sidebar-splitter:hover,
richlistitem:not([selected=true]):hover,
.feed-folder:not([selected=true]) .folder-title-container:hover,
richtreeitem:not([selected=true]):hover
{
	border-color: rgb(91, 146, 213) !important;
}

/* */
#feed-view-header
{
	background: 0 !important;
	padding: 4px !important;
}
#feed-view-header > *
{
	margin: 0 !important;
	padding: 0 !important;
}
#searchbar
{
	margin: 0 0 0 4px !important;
	padding: 0 4px 0 4px !important;
}
.view-button
{
	border: 1px solid transparent !important;
	border-top: 0 !important;
	border-bottom: 0 !important;
}
.view-button:not([disabled])[checked],
.view-button:not([disabled]):active
{
	border-color: rgb(120, 152, 176) !important;
}

/* body */
BODY
{
	background: 0 !important;
	margin: 0 !important;
	padding: 4px !important;
}
#message-box,
#main-message,
#secondary-message,
#feed-content,
.entry,
.day-header,
.title-link,
.headline-container,
.headline-header,
.headline-title,
.full-container,
.header,
.subheader,
.content,
IMG
{
	min-width: none !important;
	max-width: none !important;
	border: 0 !important;
	margin: 0 !important;
	padding: 0 !important;
	background: 0 !important;
	color: rgb(255, 255, 255) !important;
}
.entry:not(:last-child)
{
	margin-bottom: 4px !important;
}
.headline-container,
.header,
.subheader,
.content
{
	padding: 0 4px 0 4px !important;
}
.header,
.subheader,
.content
{
	padding-bottom: 2px !important;
}
.header
{
	padding-top: 2px !important;
}

.day-header
{
	margin-left: 4px !important;
	color: rgb(64, 200, 64) !important;
}
.day-header,
.headline-feed-name,
.headline-title,
.title-link,
.feed-name,
.date
{
	font-size: 14px !important;
}
.search-highlight
{
	border-radius: 1.5px !important;
	border: 1px solid rgb(255, 255, 255) !important;
	color: rgb(64, 64, 200) !important;
	background: rgb(0, 0, 0) !important;
}

.entry
{
	border-radius: 1.5px !important;
	border: 1px solid rgb(255, 255, 255) !important;
	background:
		-moz-linear-gradient(
			rgb(0, 0, 0),
			rgb(128, 128, 128)
		) !important;
}
.header
{
	background: rgb(255, 255, 255) !important;
}
.title-link
{
	color: rgb(0, 0, 0) !important;
}

/* Hover */
.entry:hover
{
	background: 
		-moz-linear-gradient(
			rgb(0, 0, 0),
			rgb(47, 84, 104)
		) !important;
}
.entry:hover .title-link
{
	color: rgb(47, 84, 104) !important;
}

/* Body Cursors */
.day-header,
.entry
{
	cursor: default !important;
}
.headline-container,
BODY.headlines-mode .header,
.title-link
{
	cursor: pointer !important;
}

}
