css:
@-moz-document url-prefix('http://www.formula1.com/live_timing/') {

#popUpContentBody {
background: #000000;
}

div.raceResultsHeading {
background: #000000;
}

h2, h3 {
color: #ffffff;
}

div.liveTimingsContainer {
background: #000000;
}

ul.liveTimingMenu {
background-color: #000000;
}

ul.liveTimingMenu li a.active {
color: #ffffff;
}

ul.liveTimingMenu li a {
color: #999999;
}

div.liveTimingsContainer .helpInner {
background: black;
color: #cccccc;
}

div.liveTimingsContainer .helpContent {
background: #000000;
color: #cccccc;
}

}
