css:
@namespace url(http://www.w3.org/1999/xhtml);







@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {





#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue, #dashboard_followers, #dashboard_members, body{

background-image:url('http://media.tumblr.com/tumblr_liledhk8kD1qzbrv5.png') !important;

background-repeat:repeat !important;

background-attachment: fixed;

background-color:#17130d !important;

background-position:top !important

}







#dashboard_index div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content, #dashboard_post_queue div.l-content, #dashboard_members div.l-content, #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

background: rgba(254,247,239,0.7) !important;

}



#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: rgba(254,247,239,0.7) !important;}



#dashboard_edit_post{

background-color: #ffffff !important

}







#right_column .dashboard_nav_item {

border-bottom:0px solid #eee !important;

background-image:none  !important



}







#right_column .dashboard_nav_item .dashboard_nav_title,

                    #right_column .dashboard_nav_item .dashboard_nav_title a,

                    #right_column .dashboard_nav_item a,

                    #right_column .dashboard_nav_item ul.dashboard_subpages,

                    #right_column .dashboard_nav_item ul.dashboard_subpages li a {

                        color:#000000 !important;



}







#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {

                        color:#888 !important ;

                    }







#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {

                        background: none !important;

                    }



#left_column #posts .notification .avatar {

                        border-color:transparent !important;

                        background-color:#ffffff !important;



                    }







#left_column #posts .notification .avatar:hover {opacity:1 !important;}



.post{opacity:1 !important; border-bottom:0px !important; color:#000000; font-size:11px; font-family:Century Gothic;}

.post_title {font-family:Lucida Handwriting !important; font-size:16px !important; color:#333;}

.post:hover{opacity:1 !important}

.post_info, .post_controls, .nav_item, a, a:active, a:visited {color:#000 !important; text-decoration:none;}

.post_info {color:#000 !important;}

.permalink{background-color: #ffffff !important; background-image:none !important}

.active{background-color:#f9ccc9 !important; color:#96a59a !important;}

.nav_item_nipple{display:none !important}

.nav_menu{background-color:#f9ccc9 !important; border-bottom:0px !important; color:#96a59a !important;}





#nav .nav_item.active,

#nav .nav_item .nav_menu,

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {

background-color:#fff !important;

background-image:none !important;

border:0px !important;

color:#7d8a81 !important;

}







#nav .nav_item .nav_menu {

border-bottom:0px !important

}







#nav .nav_item .nav_menu a {

color:#c18d6a !important;

border-top-color:#322115 !important;

}



#nav .nav_item .nav_menu a:hover {

color:#7d8a81 !important;}





.dashboard_switch_blog_menu_header{

background-color:#17130d !important;

color:#777 !important;

}



.dashboard_switch_blog_menu_item{

background-color:#ffffff !important;

color:#777 !important;

border:0px !important

}



.dashboard_switch_blog_menu_footer{

background-color:#ffffff !important;

color:#777 !important;

}



.dashboard_switch_blog_menu_wrapper{

background-color:#ffffff !important;

}







#new_post_notice_container{

color:#fff !important

}







#new_post_notice_container a{

color:#fff !important

}







#dashboard_switch_blog_menu_current,

#dashboard_switch_blog_arrow_and_divider,

#dashboard_switch_blog_arrow_current

{background-image:none !important}





#footer{

background: #ffffff !important;

background-image:none !important

}





/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
.new_post {
	background: #ffffff url('http://i1024.photobucket.com/albums/y302/BlahRandom/OhTumblr/travelandexploretextposticons394-sk.png') center center no-repeat !important;
}
.new_post_label_icon {
	background-image: none !important;
}











.new_post_label{

font-size:12px !important;

padding-top:5px !important;

font-family:Century Gothic !important;

text-transform: none !important;

color:#333 !important;

letter-spacing:1px !important

}



#left_column #posts .notification {

                        background-color:#fef7ef !important;

                        color:#FFCCCC !important;

                        border-bottom-color: #FFCCCC !important

                    }







#left_column #posts .notification .block {

                            color:#FFCCCC !important;

                        }







#left_column #posts .notification.alt {

                        background-color: #fef7ef !important;

border-bottom-color: #ffcccc !important

                    }



#left_column #posts .notification.last_notification,

                    #left_column #posts .notification.single_notification {

                        border-bottom-color:#FFCCCC !important;

                    }



#left_column #posts .notification a {

                        color:#68574a !important;

                        border-bottom:0px !important;

                    }



#left_column #posts .notification blockquote {

                        border-left-color:#eee !important;

                    }







#left_column #posts .notification .avatar {

                        border-color:transparent !important;

                        background-color:#fbfbfb !important;

                    }







#left_column #posts .notification .avatar:hover{

                    }

.notification_type_icon{display:none !important}





h1,h2,h3,h4,h5,h6{color:#efa061 !important}



}



#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {

background: transparent !important;

padding: 5px;

}



#right_column .dashboard_nav_item .dashboard_controls_radar_media {

overflow: hidden;

width: 190px;

padding: 4px 6px;

margin: 10px auto 0 auto;

background: transparent !important;

font: normal 11px 'Century Gothic';

line-height: 16px;

color: #1F2B38;

word-wrap: break-word;

position: relative;

}



#right_column .dashboard_nav_item #hide_radar {

position: absolute;

top: 0;

right: 0;

width: 45px;

height: 15px;

background: transparent !important;

text-align: right;

display: none;

}



#posts .post .footer_links {

font: 11px 'Century Gothic' !important;

}

#posts .post .post_info, #posts .post .post_controls {

font: 11px Century Gothic;

color: #A8B1BA;

min-height: 16px;

margin-bottom: 15px;

line-height: 15px;

letter-spacing:1px;

}



#right_column .dashboard_nav_item {font:11px 'Century Gothic' !important;}

#right_column a#dashboard_switch_blog_menu_current {

font: 20px Georgia;}





.dashboard_options_form {background-color:#fef7ef !important; color:#FFCCCC !important; border-bottom-color: #FFCCCC !important;}



form.dashboard_options_form .dashboard_options_form_divider {

display:none;

}
