@-moz-document url-prefix('http://www.facebook.com'), url-prefix('https://www.facebook.com') {
	/*Topp meny*/
	
	#blueBar {
		position: fixed !important;
		width: 1010px !important;
		z-index: 98 !important;
		top: 5px !important;
		height: 39px !important;
		margin-left: -505px !important;
		left: 50% !important;
	}
	#blueBar.loggedOut {
		visibility: hidden !important;
	}
	.loggedout_menubar_container {
		padding-top: 20px !important;
	}
	
	#pageHead .clearfix div.lfloat {
		float:right !important;
	}
	
	.rfloat li a, .rfloat li form label {
		margin: 0 5px !important;
	}
	
	.rfloat li a, .rfloat li form label input {	
		-moz-border-radius: 2px !important;
		-webkit-border-radius: 2px !important;
	}
	
	#pageHead {
		position: fixed !important;
		z-index: 99 !important;
		top: 0 !important;
	}
	
	#headNav {
		background-color: #3B5998 !important;
		min-width: 799px !important;
		border: none !important;
	}
	
	#pageLogo a:hover, #pageLogo a:focus, #pageLogo a:active {
		-moz-border-radius: 2px !important;
		-webkit-border-radius: 2px !important;
	}
	#pageHead .fbJewel .jewelButton {
		-moz-border-radius: 2px !important;
		-webkit-border-radius: 2px !important;
	}
	
	.fbJewel .jewelItemList a, .fbJewel .jewelFooter a {
		-webkit-transition: background-color 0.1s linear, color 0.1s linear !important;
	}
	.fbJewel .jewelHighlight li a:hover, .fbJewel .jewelHighlight li a:active, .fbJewel .jewelHighlight li a:focus {
		background-color: #829ACE !important;
		border-bottom: 1px solid #829ACE !important;
		border-top: 1px solid #829ACE !important;
	}
	.fbJewel .jewelFlyout {
		border-bottom: 1px solid #333; !important;
		-webkit-border-bottom-left-radius: 5px !important;
		-webkit-border-bottom-right-radius: 5px !important;
		-moz-border-radius-bottomleft: 5px !important;
		-moz-border-radius-bottomright: 5px !important;
		border-bottom-left-radius: 5px !important;
		border-bottom-right-radius: 5px !important;
	}
	
	/*Profilsidan för grupper*/
	.groupProfileHeaderWash {
		width: 790px !important;
		margin-top: -22px !important;
	}
	#pagelet_group .topborder {
		border:none !important;
	}
	/*Old groups*/
	.editgroup .inactivetab a {
		-webkit-transition: background-color 0.2s linear !important;
		-moz-transition: background-color 0.2s linear !important;
	}
	
	/*Video*/
	.video_permalink #video_info {
		padding: 0 !important;
		margin-right: -20px !important;
	}
	
	/*Justera så inte innehållet hamnar under menyn
	 * fixar en border
	 * Marginaler*/
	
	#content {
		position: relative !important;
		top: 41px !important;
		background: white !important;
	}	.profile #content, .HC_Top_3 #content {margin-left:0 !important;} .photo #content{margin-left: 20px;}
	#contentCol {
		background-color: transparent;
	}
	#globalContainer{
		background: white !important;
		border-right: 1px solid #B3B3B3 !important;
		border-left: 1px solid #B3B3B3 !important;
		position: relative;
	}	#globalContainer {width: 981px !important;}
	
	.hasLeftCol #globalContainer {
		border-left:none !important;
	}
	.hasLeftCol #mainContainer {
		border-right: none !important;
	}
	
	.fbx #rightCol {
		padding-top: 0 !important;
		margin-top: -22px !important;
	}
		/*Help Center main page*/
		.HC_Top_3 .HC_Nav, .HC_Top_3 .HC_Nav .pam {
			border: none !important;
		}
		.HC_Top_3 .HC_Nav .uiList {
			border-top: 1px solid #ECECEC !important;
			border-bottom: 1px solid #ECECEC !important;
			padding-bottom:0 !important;
		}
		.HC_Top_3 .HC_Nav .uiListItem {
			display: block !important;
			padding: 10px !important;
			
			-webkit-transition: background-color 0.2s linear !important;
			-moz-transition: background-color 0.2s linear !important;
		}
		.HC_Top_3 .HC_Nav .uiListItem:hover {
			background: #ECECEC !important;
		}
		
	/*Header Area*/
	#headerArea {
		margin: 0 0 20px 20px !important;
	}
	/*#pagelet_header {
		margin-left: 15px !important;
	}*/
	/*ui header*/
	.uiHeaderSection, .uiSideHeader {
		background-color: transparent !important;
		border-top:none !important;
		border-bottom: 1px solid #CFCFCF !important;
	}
	/*Left Col*/
	.uiSideNav .buttonWrap {
		left: -11px !important;
	}
	#leftCol {
		padding: 20px 0 0 !important;
		width: 180px !important;
		min-height: 500px !important;
		
		border-left: 1px solid #B3B3B3 !important;
		border-bottom: 1px solid #B3B3B3 !important;
		border-bottom-left-radius: 5px !important;
		-moz-border-radius-bottomleft: 5px !important;
		-webkit-border-bottom-left-radius: 5px !important;
	}
	#leftCol .sideNavItem a {
		-webkit-transition: background-color 0.2s linear !important;
		-moz-transition: background-color 0.2s linear !important;
	}
	.home #pagelet_friends_online .pas {
		position: relative !important;
		left: 3px !important;
	}
	#leftCol #pagelet_welcome_box {
		position: relative;
		left: 10px;
	}
	.profile-picture {
		width: 181px !important;
	}

	/*Profile pic left col*/
	.profile-picture:hover .edit_profilepicture {
		opacity: .7 !important;
	}
	.edit_profilepicture {
		-webkit-transition: opacity 1s ease-out;
		opacity: 0 !important;
		display: block !important;
	}
	/*Right Col*/
	.hasLeftCol #rightCol {
		padding-right: 10px !important;
	}
	.egoOrganicColumn {
		margin-top:22px !important;
	}
	#pagelet_ego_pane .egoOrganicColumn {
		margin-top:0 !important;
	}
	/*Write status*/
	#pagelet_composer, .profile-pagelet-section #profile_stream_composer, #pagelet_group #pagelet_group_composer {
		margin-left: 8px !important;
		width: 475px !important;
	}
	/*Post boxen home*/
	
	#pagelet_home_stream {
		width: 520px !important;
		position: relative !important;
		left: -14px !important;
	}
	
	.uiStream .pvm, .uiStream .phs .mtm .ptm, #pagelet_question_stream .mtm .fbQuestionProfileRow, #pagelet_group .mall_post {
		padding: 10px !important;
		background: #F2F2F2 !important;
		margin: 10px !important;
		display: block !important;
		border: none !important;
	
		/* Firefox */  
		-moz-border-radius: 5px !important;  
		/* Webkit-baserade webbläsare */  
		-webkit-border-radius: 5px !important;
	
		-webkit-transition: background-color 0.25s linear !important;
		-moz-transition: background-color 0.25s linear !important;
	}
	
	.uiStream .pvm:hover, .uiStream .ptm:hover, #pagelet_question_stream .fbQuestionProfileRow:hover, #pagelet_group .mall_post:hover {
		background: #FAFAFA !important;
	}
	
	/*.uiListLight, .uiListMedium, .uiListDark {
		border-style: none !important;
		color: #BDBDBD !important;
	}*/
	
	.storyContent .uiBlingBox {
		border:none !important;
	}
	
	/*Post boxen profile*/
	#profile_minifeed li.pvm {
		position: relative !important;
		left: -14px !important;
	}
	
	/*Post box permalink*/
	.permalink_stream li.pvm, #profile_minifeed li.pvm  {
		width: 480px !important;
	}
	
	.mvm { /* Länkarna över posten */
		margin-left: 15px !important;
	}
	
	/*Innehållet i posten, meddelandet och kommentarsfältet*/
	
	.UIImageBlock_Content {
		max-width: 390px !important;
	}
	
	/*Radera posten knappen*/
	
	.uiStream .hideButton, .uiStream .hideSelector {
		position: relative !important;
		right: 5px !important;
	}
	.UIImageBlock_ICON_Content .mlm {
		right: -5px !important;
	}
	.uiSelector .uiCloseButton {
		border:none !important;
		-webkit-border-radius: 1px !important;
		-moz-border-radius: 1px !important;
	}
	#pagelet_app_stories .uiStream .hideButton, #pagelet_app_stories .uiStream .audienceIndicator, #pagelet_app_stories .uiStream .hideSelector {
		margin: -2px -10px 0 0 !important;
	}
	/*Meddelandet i posten*/
	
	.uiStreamMessage {
		max-width: 380px !important;
	}
	
		/*Kommentarsfältet*/
	
	.commentable_item {
		padding-top: 10px !important;
	}
	/*Storleken på kommentarsfältet*/
	.UIImageBlock .uiUfi {
		position: relative !important;
		right: 51px !important;
		width: 460px !important;
	} #pagelet_group .uiUfi {width: 435px !important;}
	
	/*Kommentars rutorna*/
	.uiUfi .ufiItem {
		background-color: #E6E6E6 !important;
		border: none !important;
		color: black !important;
		
		/* Firefox */  
		-moz-border-radius: 5px !important;  
		/* Webkit-baserade webbläsare */  
		-webkit-border-radius: 5px !important;
	} .uiUfi {padding-top: 10px !important;}
	
	/*Comments effects*/
	.storyInnerContent img.actorPic {
		-webkit-transition: width 1s linear, height 1s linear !important;
		width: 0 !important;
		height: 0 !important;
		display: block !important;
	}
	.storyInnerContent .child_is_active img.actorPic {
		width: 32px !important;
		height: 32px !important;
	}
	
	/*Post status effects*/
	.uiComposer .showOnceInteracted{}
	
	
	/*Event side*/
	#event_info_pagelet {
		margin-right: 20px !important;
		margin-left: -10px !important;
	}
	.eventsWhiteComposer #feedwall_with_composer ul#home_stream {
		width: 520px !important;
		margin-left: -15px !important;
	}
	/*Album side*/
	ul.uiList .uiUfi .focus_target .fbUfi {
		right:0 !important;
	}
	#pagelet_photo_albums {
		margin-top: -22px !important;
	}
	.photo .pagerpro_container .pagerpro_li {
		border:none;
	}
	/*More posts*/
	#pagelet_stream_pager {
		margin: 10px !important;
	}
	.uiBoxLightblue {
		background: #E6E6E6 !important;
		border: none !important;
		
		/* Firefox */  
		-moz-border-radius: 5px !important;  
		/* Webkit-baserade webbläsare */  
		-webkit-border-radius: 5px !important;
		
		-webkit-transition: background-color 0.25s linear !important;
		-moz-transition: background-color 0.25s linear !important;
	}
	#profile_pager {
		width: 500px !important;
		position: relative !important;
		right: 4px !important;
	}
	.uiBoxLightblue:hover {
		background: #D9D9D9 !important;
	}
	/*Posta*/
	.uiComposerMessageBoxMentions .inputContainer {
		width: 475px !important;
	}
	/*Game side*/
	#pagelet_app_stories .uiPillButton {
		line-height: 15px !important;
		padding: 6px 10px !important;
		-webkit-border-radius: 2px !important;
		-moz-border-radius: 2px !important;
		-webkit-transition: background-color 0.1s linear, color 0.1s linear !important;
		-moz-transition: background-color 0.1s linear, color 0.1s linear !important;
	} #pagelet_app_stories .uiPillButton:hover { background: #95A9D6 !important; color: white !important;}
	
	#pagelet_app_stories .uiPillButtonSelected {
		padding: 5px 10px 5px !important;
		background-color: #3B5998 !important;
		padding: 6px 10px !important;
		border: none !important;
	} #pagelet_app_stories .uiPillButtonSelected:hover {background-color: #3B5998 !important;}
	
	#app_stories .UIImageBlock .uiUfi {
		width: 435px !important;
	}
	
	/*Notes*/
	#pagelet_notes .note_content {
		color: black !important;
	}
	
	/*Meddelanden*/
	#MessagingLoadingSpinner, .MessagingForwardedContent, .MessagingReadViewMainContent, #MessagingShelf {
		float:left !important;
	}
	
	/*Inkorg*/
	.threadRow.unread, .threadRow.unread + .threadRow {
		margin: 0 !important;
		padding: 0 10px 0 !important;
		margin-left: -10px !important;
	}
	.uiListLight, .uiListMedium, .uiListDark {
		padding-right: 10px !important;
	}
	/*Edit Profile*/
	.fbPageEdit .grayArea {
		margin-right: 9px !important;
	}
		/*Sponsor*/
	
	.ego_column {
		visibility: hidden !important;
	}
	.egoOrganicColumn {
		visibility: visible !important;
	}
	.profile #pagelet_ads {
		visibility:hidden !important;
	}
	
		/*Footer*/
	
	#pageFooter {
		margin-top: 40px !important;
	} .profile #pageFooter {margin-left: 170px !important;}
	
	.hasLeftCol #contentCurve {
		border-right: none !important;
		border-left: 1px solid #B3B3B3 !important;
		
		border-bottom-left-radius: 5px !important;
		-moz-border-radius-bottomleft: 5px !important;
		-webkit-border-bottom-left-radius: 5px !important;
	}
	#footerContainer {
		border:none !important;
		margin-bottom:30px !important;
	}
	
	.mrl {
		margin-left: 20px !important;
	}
	
	/*Hover popup*/
	.hovercard .stage {
		border: 1px solid #333 !important;
		/* Firefox */  
		-moz-border-radius: 5px !important;  
		/* Webkit-baserade webbläsare */  
		-webkit-border-radius: 5px !important;
	}
	.hovercard .actions {
		-webkit-border-bottom-left-radius: 5px !important;
		-webkit-border-bottom-right-radius: 5px !important;
		-moz-border-radius-bottomleft: 5px !important;
		-moz-border-radius-bottomright: 5px !important;
		border-bottom-left-radius: 5px !important;
		border-bottom-right-radius: 5px !important;
	}
	/*Write popup*/
	.commentBox .uiTypeaheadView{
		-webkit-transition: height 0.2s linear !important;
	}
	.commentBox .uiTypeaheadView .user {
		-webkit-transition: background-color 0.2s linear !important;
		-moz-transition: background-color 0.2s linear !important;
	}
	.uiTypeaheadFocused .uiTypeaheadView {
		height: auto !important;
	}
	.commentBox .uiTypeaheadView {
		height: 0 !important;
		display: block !important;
		-webkit-transition: height 0.2s linear !important;
	}
	.commentBox .uiTypeaheadView .compact {
		border-width: 1px !important;
		-webkit-border-bottom-left-radius: 5px !important;
		-webkit-border-bottom-right-radius: 5px !important;
		-moz-border-radius-bottomleft: 5px !important;
		-moz-border-radius-bottomright: 5px !important;
		border-bottom-left-radius: 5px !important;
		border-bottom-right-radius: 5px !important;
		
		-webkit-transition: height 0.2s linear !important;
	}
	
	/*Chatt*/
	.sidebarMode #globalContainer, .sidebarMode #ConfirmBannerOuterContainer {
		left: 0 !important;
	}
	.fbChatSidebar {
		border-left: none !important;
		-webkit-box-shadow: none !important;
		background-color: white !important;
		width: 184px !important;
	}
	.fbChatSidebar .fbChatOrderedList {
		top: 55px !important;
	}
	.og .fbNubFlyoutTitlebar {
		-webkit-border-top-left-radius: 5px !important;
		-webkit-border-top-right-radius: 5px !important;
		-moz-border-radius-topleft: 5px !important;
		-moz-border-radius-topright: 5px !important;
		border-top-left-radius: 5px !important;
		border-top-right-radius: 5px !important;
	}
	.og .fbNubFlyout {
		background-color: transparent !important;
	}
	.og .fbNubFlyoutBody {
		background: white !important;
	}
}
