 .sidebarLinks li { width:100% !important; text-align: center !important; font-size: 10pt !important; }
 
 .top_right_container, .alsoOnFark, .shoprotator
  { height: 0px !important; display: none !important; }

 #logo, #mainLogo, #foobieslogo, #totalfarklogo, #totalfarklogo.retina,
 #footerFarkLinks, #alsoOnFarkTable, #wtf_frame,
 #topAd728x90, #rightAd300x250, #rightAd300x250Lower, 
 #facebook, #twitter, #twitter-widget-0, #rss, #gplus, #pinterest,
 #boxSwap, #feat_ph, #voterreg_frame, #adPleaBar, #abPleaBar,
