css:
@-moz-document domain('ponychan.net') {
body {
    background-image:url("http://img263.imageshack.us/img263/1701/18730pngflipped.png") !important;
    color: #98B1D7 !important;
    background-color: #000000 !important;
    background-repeat:no-repeat !important;
    background-attachment:fixed !important;
    background-position: top right !important;
}

.logo {
    color: #98B1D7 !important;
    font-family: "Capacitor", Courier, monospace !important;
}
#verytopbar, #themedropdown, #watchedthreads {
	-moz-box-shadow: 0px 0px 0px !important;
	box-shadow: 0px 0px 0px !important;
	-webkit-box-shadow: 0px 0px 0px !important;
}
a {
    color: #98B1D7 !important;
}
a:hover, .reflink a:hover {
    color: #E3ECF2 !important;
}
#verytopbar, .adminbar, #themedropdown {
    background:url("http://img263.imageshack.us/img263/1701/18730pngflipped.png") !important;
    background-color: #000000 !important;
    background-repeat:no-repeat !important;
    background-attachment:fixed !important;
    background-position: top right !important;
}
#verytopbar a {
    border-top-color: #393D63 !important;
    color: #98B1D7 !important;
}
#verytopbar a:hover {
    background: none repeat scroll 0 0 #30364D;
    color: #E3ECF2 !important;
}
#verytopbar a:hover, #verytopbar .navbarcurboard {
    border-top-color: #7479BD !important;
}
#verytopbar h1 {
    text-shadow: 1px 1px 1px #000;
    color: #FFFEFF !important;
    font-weight: lighter !important;
}
#verytopbar h3 { 
    font-size: 0px !important;
}
h3:after { 
    color: #98B1D7 !important;
    display: block !important;
    font-style: italic !important;
    font-size: 24px !important;
    content: '"Now 20% LOUDER!"' !important;
}
#sitesidebar a:hover, #topmenu li {
    opacity: 0.65 !important;
    background:url("http://img263.imageshack.us/img263/1701/18730pngflipped.png") !important;
    background-color: #000000 !important;
    background-repeat:no-repeat !important;
    background-attachment:fixed !important;
    background-position: top right !important;
}
#sitesidebar h2, .newsentry h2, #topmenu li.tab {
    background: none repeat scroll 0 0 #000000 !important;
    border: 1px solid #889DBF !important;
    border-width: 1px 2px 2px 1px !important;
    border-style: solid !important;
    opacity: 1 !important;
}

#topmenu li.current, #sitesidebar h2 {
    background: none repeat scroll 0 0 #000000 !important;
    border: 1px solid #889DBF !important;
    border-width: 1px 2px 2px 1px !important;
    border-style: solid !important; 
    opacity: 1 !important;
}
.postblock {
    background: none repeat scroll 0 0 #F0EFF0 !important;
    opacity: 0.70 !important;
    -moz-border-radius: 10px !important;
    border-radius: 10px !important;
    -webkit-border-radius: 10px !important;
    background:url("http://img534.imageshack.us/img534/4545/20903pngflipped.png") !important;

}
.postarea input[type="text"], .postarea input[type="password"], .postarea textarea {
    border-color: #56658E !important;
    opacity: 0.70 !important;
    -moz-border-radius: 10px !important;
    border-radius: 10px !important;
    -webkit-border-radius: 10px !important;
    color: #98B1D7 !important;
    background:url("http://img534.imageshack.us/img534/4545/20903pngflipped.png") !important;
    padding: 0px 5px !important;
}
.postarea input[type="text"]:focus, .postarea input[type="password"]:focus, .postarea textarea:focus {
    border-color: #56658E !important;
    color: #98B1D7 !important;
    background:url("http://img534.imageshack.us/img534/4545/20903pngflipped.png") !important;
    padding: 0px 5px !important;
}

.postarea textarea {
    background-repeat: no-repeat !important;
    background-position: right center !important;
    color: #98B1D7 !important;
    background:url("http://img534.imageshack.us/img534/4545/20903pngflipped.png") !important;
}

.helphover div, #themedropdown, .reply, .highlight, .reflinkpreview {
    background: none repeat scroll 0 0 #000000 !important;
}
.helphover div, #themedropdown {
    border: 1px solid #414B69 !important;
    border-width: 1px 2px 2px 1px !important;
    border-style: solid !important; 
    -moz-border-radius-bottomright:12px !important;
    -moz-border-radius-topleft:12px !important;
    -webkit-border-bottom-right-radius:12px !important;
    -webkit-border-top-left-radius:12px !important;
    border-bottom-right-radius:12px !important;
    border-top-left-radius:12px !important;
}
.highlight, .reflinkpreview {
    border-left-color: #56658E !important;
    opacity: 0.90 !important;
    -moz-border-radius: 10px !important;
    border-radius: 10px !important;
    -webkit-border-radius: 10px !important;
}
.filetitle, .newstimestamp {
    color: #FFFEFF !important;
}
.postertrip, .postername, .postername a {
    color: #FFFEFF !important;
    -moz-border-radius: 10px !important;
    border-radius: 10px !important;
    -webkit-border-radius: 10px !important;
}
.filesize {
    color:#98B1D7 !important;
}
.unkfunc {
    color: #E3ECF2 !important;
}
.recentpostitem, .reply {
    background:url("http://img534.imageshack.us/img534/4545/20903pngflipped.png") !important;
    background-color: #000000 !important;
    background-repeat:no-repeat !important;
    background-attachment:fixed !important;
    background-position: top right !important;
    border: 1px solid #C7D9E5 !important;
    border-width: 1px 2px 2px 1px !important;
    border-style: solid !important; 
    -moz-border-radius-bottomright:12px !important;
    -moz-border-radius-topleft:12px !important;
    -webkit-border-bottom-right-radius:12px !important;
    -webkit-border-top-left-radius:12px !important;
    border-bottom-right-radius:12px !important;
    border-top-left-radius:12px !important;
    padding: 0px 5px !important;
    color: #98B1D7 !important;
    opacity: 1.00 !important;
}
.highlight, .reflinkpreview {
    background:url("http://img11.imageshack.us/img11/3964/97031pngflipped.png") !important;
    background-color: #000000 !important;
    background-repeat:no-repeat !important;
    background-attachment:fixed !important;
    background-position: top right !important;
    border: 1px solid #C7D9E5 !important;
    border-width: 1px 2px 2px 1px !important;
    border-style: solid !important; 
    -moz-border-radius-bottomright:12px !important;
    -moz-border-radius-topleft:12px !important;
    -webkit-border-bottom-right-radius:12px !important;
    -webkit-border-top-left-radius:12px !important;
    border-bottom-right-radius:12px !important;
    border-top-left-radius:12px !important;
    padding: 0 30px 20px 0 !important;
    color: #98B1D7 !important;
    opacity: 1.00 !important;
}
.spoiler {
    background-color: #3C4764 !important;
    color: #3C4764 !important;
}
.spoiler:hover { 
    background-color: transparent !important;
    color: #98B1D7 !important;
}}
