css:
@namespace url(http://www.w3.org/1999/xhtml);
/*漢典精简版*/
@-moz-document domain("zdic.net") {
	body,html{
   	  background:#000;
  	   color:#666;
	}
	.sec_m,#tp_ts1,
	#sidebar,#footer,#topmenu,#header,#sitelogo,.annu_div,.ga{
		display:none !important;
	}
	#content{
		margin-right:0 !important;
	}
	#z_i_1>tbody>tr>td:nth-child(3){ display:none; }
	.secpan_qb .query,input#q{ width:100px!important;}
	.secpan_qb .btn{float:left;}
	
}

/*经典论坛 去掉影响视觉的东西*/

@-moz-document domain("bbs.classic023.com") {
 .ducebox,/*首页推荐*/
 .ad_column,/*商务合作处的广告*/
 .ad_footerbanner,/*首页页脚广告*/
 .content .mainbox:nth-child(8),/*商务合作*/
 .content .mainbox:nth-child(9),/*商务合作*/
 .content .mainbox:nth-child(10),/*商务合作*/
 .t_msgfontfix tr>td:nth-child(2),.ad_textlink2,
 body>div:nth-child(4),/*最顶上的链接*/
 #forumlinks,/*友情链接*/
 #header>div:first-child,/*banner*/
 #menu, 
 .postbottom,/*帖子广告*/
 #ad_text,/*上方广告*/
 #focus  /*推荐*/
 {
   display:none;
 }
}

/*无广告地铁站*/ 

@-moz-document domain("www.ditiezu.com") {
   #ad_headerbanner,#footer ,.useraction,.postbottom,iframe{display:none;}
}

/*中医世家清理*/

@-moz-document domain("zysj.com.cn") {
   .Qmg,iframe,.Qr,div.Qo{display:none;}
}


/*华夏中医*/

@-moz-document domain("bbs.hx317.com") {
  .a_mu,.a_f,iframe,.scbar_hot_td,.a_pt,.a_pr,.a_pb,.bm{display:none;}
}
