css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sguni.pl") 
{


/* Backgrounds */
/*html, body { background: url('http://img855.imageshack.us/img855/4357/tech3.jpg') !important; }*/

/* Fonts */
a, h1 > .Title:nth-child(1) span {color: #99ccff !important; text-decoration: none !important; text-shadow: 0px 0px 2px #000000 !important;}
a:hover, a.Highlight {text-shadow: 0px 0px 3px #ffffff !important;}

#content-wrapper > article > p {text-shadow: 0px 0px 2px #000000, 1px 1px 2px #000000 !important;}
.Discussions, .Discussion, article {text-shadow: 0px 0px 2px #000000 !important;}


h1, h2, h4, h1 > .Title:not(:nth-child(1)) span, .SubTab {color: #ffffff !important; text-shadow: 0px 0px 2px #000000 !important;}

/*.edit-link, .delete-link {color: #6699ff !important;}
span, .Excerpt {color: #aaaaaa !important;}
.Tabs li span, .Meta strong, span.Announcement, .About dt {color: #ffffff !important;}*/

.good-rating {color: #99ffcc !important;}
.ok-rating, .ok-rating a:not(:hover), .no-rating, .no-rating a:not(:hover) {color: #6699ff !important;}

.bad-rating {color: #ff6666 !important;}
.bad-rating a:not(:hover) {color: #ff6666 !important;}
.obsolete, .obsolete a:not(:hover) {color: #666666 !important;}

#style-author, .edit-link, .delete-link {font-size: 0.85em !important;}
#style-author > div:nth-child(1) > a {font-size: 1.5em !important;}


/* Box Shadow */
#donate input[type="image"] {box-shadow: 0px 0px 3px 4px #99ccff !important;}
header, article > ul li:hover, #front-page-best h2, #front-page-newest h2, #front-page-best li:hover, #front-page-newest li:hover {box-shadow: 0px 0px 1em 0px #000000 !important;}
.Box {box-shadow: none !important;}

/* Size & positioning */
#Menu li a {height: 24px !important; }


/* Padding */
#style-author, .pagination, #search-options, #long-description {padding: 10px !important;}
article > ul {padding: 10px 10px 10px 30px !important;}
#front-page-best h2, #front-page-newest h2 {padding: 0px 5px !important;}
#imageContainer {padding-top: 20px !important; padding-bottom: 0px !important;}


/* Buttons */
.BigButton, .Tabs li a, #Menu li a, #category-search-submit, input[value="Save"], input[type="submit"], input[type="button"], button, #search-submit {color: #99ccff !important; border: 0px !important;border-color: #666666 !important; -moz-border-radius: 7px !important; background: rgba(40,40,40,.5) !important; -moz-box-shadow: inset #666666 0px 1px 1em 0px, #000000 -1px -1px 4px, #000000 1px 1px 4px !important;-moz-appearance: none !important; text-shadow: 0px 0px 2px #000000 !important;}

.BigButton:hover, .Tabs li a:hover, .Tabs li.Active a, #Menu li a:hover, #category-search-submit:hover, input[value="Save"]:hover, input[type="submit"]:hover, input[type="button"]:hover, button:hover, #search-submit:hover {border-color: #6699cc !important; background: rgba(40,40,80,.5) !important; -moz-box-shadow: inset #6699cc 1px 1px 1em 0px, #006699 -1px -1px 1em, #006699 1px 1px 1em !important;color:#99ccff !important; text-shadow: #99ccff 0px 0px 1px, #ffffff 0px 0px 2px !important;}

/* Dark input fields */
#username, #password, input, select, input[name="search_terms"], #search-terms, textarea, input[type="text"], #password{background: #000000 !important;-moz-appearance:none!important; color: #00FF00 !important; border: #000000 !important; padding: 2px !important;}





}
