css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://xkcd.com),
url-prefix(http://www.xkcd.com) {

* {
  font-family:ubuntu !important;
}
body, .bd, .c {
  background:transparent !important;
}
h1 {
  font:700 14pt/1.5 ubuntu !important;
}
#middleFooter, .hd, .ft, h3, .s br:nth-child(odd) {
  display:none !important;
}

#topContainer * {
  padding:0 !important;
  margin:0 !important;
  background:transparent !important;
}
#topContainer {
  height:83px !important;
  position:fixed !important;
  bottom:0 !important;
  left:0 !important;
  right:0 !important;
}
#topLeft {
  width:60% !important;
}
#topLeft ul li {
  float:left !important;
  height:83px !important;
  display:block !important;
  padding:60px 0 0 12px !important;
}
#topLeft ul li a {
  font-size:10pt !important;
  text-transform: lowercase !important;
}
#topRight, #topRightContainer {
  width:185px !important;
  height:83px !important;
  overflow:hidden !important;
}
#topRight #logo img {
  opacity:.2 !important;
}
#topRight #logo img:hover {
  opacity:1 !important;
}
#topRight h2 {
  display:none !important;
}

.menuCont * {
  padding:0 !important;
  margin:0 !important;
  border:0 !important;
}
.menuCont {
  position:absolute !important;
  padding:4px !important;
  top:0 !important;
  left:20% !important;
  right:20% !important;
  text-align:right !important;
}
.menuCont ul {
  width:100% !important;
}
.menuCont li {
  text-align:right !important;
  float:none !important;
  display:inline !important;
}
.menuCont li a {
  display:inline !important;
  padding:4px 10px !important;
  border-radius:0 0 6px 6px;
  text-transform: lowercase !important;
}
.menuCont li a:hover {
  padding-bottom:7px !important;
  color:#FFF !important;
  background:#9FB1D1 !important;
}
.menuCont ul li:nth-child(1),
.menuCont ul li:nth-child(2) {
  float:left !important;
  margin-right:4px !important;
}
.menuCont ul li:nth-child(3) a {
  display:none !important;
}

}
