css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.bbc.co.uk/iplayer) {

#blq-bbc-homelink,
#blq-accesslinks,
#bip-toolbar-container,
#blq-banner,
#bip-secondary,
#bip-footer,
#blq-footer,
#mip-downloads,
#blq-obit,
#go
{ display: none !important; }

#blq-main { 
  position:absolute !important;
  top:4px !important;
  left:0px !important;
  padding-left: 0px !important;
  width: 525px !important;
}

#blq-content {
  float: left !important;
  width: 525px !important;
}

#iph-grid {
  background-color: transparent !important; 
  padding: 0px !important;
  width: 525px !important;
}
#iph-grid #results { padding: 0px !important; float: left;}
#iph-grid #results .result.rowFirst { padding-left: 0px !important; }
#iph-grid #results .result.rowLast { padding-right: 0px !important; }

#bip-content
{background-color: transparent !important;}
#mip-container,
#mip-content
{ margin: 0px !important;
padding:0px !important; 
background-image: none !important; 
background-color: transparent !important;
  width: 525px !important;
}

#blq-nav {
  top:-20px !important;
  left:0px !important;
  width: 525px !important;
  text-align:right !important;
}

#blq-nav #bip-nav ul { padding: 0px !important;display: inline !important;}
#blq-nav #bip-nav li { display: inline !important;}
#blq-nav #bip-nav .nav-button {  display: inline !important;}

#blq-nav #bip-nav h2
{
  display: none;
}
#blq-nav #bip-nav p
 {
  display:inline;
}
#blq-nav #bip-nav .nav-button a,
#blq-nav #bip-nav .nav-button div {
  background-image: none !important;
  border-width: 0px !important;
  display: inline !important;
}

#blq-nav #bip-nav .nav-button span {
  display: none !important;
}

#resultsnav {
  margin-left: 0px !important;
  width: 500px !important;
}

#pager a#pager-prev {
  left:465px !important;
}

#bip-browse ul {
 top: 2px !important;
 background-color: #111 !important;
 border-width: 1px !important;
 padding-left: 10px !important;
 padding-top: 10px !important;
 width: 510px !important;
}

#bip-browse ul li a {
  border-width: 0px !important;
}

}
