/* +++++++++++++++++++++ */
/* computerbase.de       */
/* +++++++++++++++++++++ */

@-moz-document domain("computerbase.de") {

body {
   margin: 0 15px 0 15px !important;
}

#page {
   margin: 5px auto 0 !important;
   width: 98.5% !important;
   max-width: 1000px !important;
}

#content {
   margin-top: 0 !important;
}

#article-teaser {
   width:605px !important;
}

#ads_topbanner {
   display: none !important;
}

#ads_skyscraper {
   display: none !important;
}

}

/* +++++++++++++++++++++ */
/* golem.de              */
/* +++++++++++++++++++++ */

@-moz-document domain("golem.de") {

  /* light gray background */
  body {
    background: none repeat scroll 0 0 #F0F0F0 !important;
  }

  /* correct offset caused by side-ad */
  #grandwrapper, .iqdSiteAlign {
    width: 980px !important;
  }

  /* smaller font in articles */
  .formatted h2, .formatted h3, .formatted h4, .formatted h5, .formatted h6, .formatted p, .formatted ol, .formatted ul {
    font-size: 14px !important;
    line-height: 21px !important;
  }

  /* smaller font in article header */
  .article article header p {
    font-size: 14px !important;
    line-height: 20px !important;
  }

  /* smaller font in headline */
  .head5 {
    font-size: 26px !important;
    line-height: 32px !important;
  }

  /* smaller font in comments */
  .article #comments p {
    font-size: 13px !important;
    line-height: 18px !important;
  }

  /* smaller font in comment headlines */
  .article #comments h2, .article #comments .dh2 {
    font-size: 14px !important;
    line-height: 18px !important;
  }

  /* smaller font in forum */
  .forum #thread-detail li p {
    font-size: 13px !important;
  }

  /* smaller font in forum headlines */
  .head2 {
    font-size: 16px !important;
    line-height: 20px !important;
    margin-bottom: 3px !important;
  }
}

/* +++++++++++++++++++++ */
/* heise.de/tp           */
/* +++++++++++++++++++++ */

@-moz-document url-prefix("http://www.heise.de/tp/") {

  /* Werbung an störenden Positionen ausblenden */
  .top-ad {
    display: none !important;
  }

  .pos-ad-high {
    display: none !important;
  }

  .ad-high {
    display: none !important;
 }

 .spalte-ad {
    display: none !important;
  }

  /* zentrieren und farbe anpassen */
  .mainframe {
    float: none !important;
    margin: auto !important;
  }

  .main {
    left: 125px !important;
    top: 80px !important;
    border-bottom: 45px solid #888888 !important;
  }

  .men-1 {
    left: 125px !important;
    top: 36px !important;
  }

  .tp-footer {
    left: 0px !important;
  }

  body {
    background-color: #888888 !important;
  }

}

@-moz-document url-prefix("http://www.heise.de/tp/foren") {

  /* keine korrektur, wenn im forum */
  .main {
    left: 0px !important;
    top: 0px !important;
  }

  .men-1 {
    left: 0px !important;
    top: 0px !important;
  }

  /* dafür andere zentrierung */
  .maintable {
    float: none !important;
    margin: 30px auto auto !important;
  }
}

@-moz-document url-prefix("http://www.heise.de/tp/blogs/foren") {

  /* keine korrektur, wenn im forum */
  .main {
    left: 0px !important;
    top: 0px !important;
  }

  .men-1 {
    left: 0px !important;
    top: 0px !important;
  }
  
    /* dafür andere zentrierung */
  .maintable {
    float: none !important;
    margin: 30px auto auto !important;
  }
}
