css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dis.4chan.org") {

.killbutton {
float: right;
display: block;
margin-top: 0px;
cursor: pointer;
opacity: 0.8;
}

.threadkillbutton {
opacity: 1;
margin-top: 2px;
}

.killbutton:hover {
opacity: 1;
}

.postform a {
display: inline-block;
margin-left: 0px; margin-right: 8px;
}

body {
background: #515B4A /* -moz-linear-gradient(rgba(0,0,0,0.3), rgba(0,0,0,0)) */ fixed !important;
color: #C8CFC4 !important;
font-family: "Segoe UI";
}

.hborder {
border: 1px #79886F solid !important;
}

.border {
border: none !important;
}

.border, .hborder {
background: #616D59 !important;
border-radius: 10px;
box-shadow: 6px 7px 18px #3F473B;
}

.head, .thread {
border: none !important;
color: #C8CFC4;
font-family: "Segoe UI";
}

.even {
background: #5A6554 /* -moz-linear-gradient(rgba(0,0,0,0.05), rgba(0,0,0,0)) */ !important;
}

.odd {
background: #6A7762 /* -moz-linear-gradient(rgba(0,0,0,0.05), rgba(0,0,0,0)) */ !important;
}

.post {
border-radius: 10px;
padding: 8px;
margin-bottom: 8px;
box-shadow: 5px 6px 10px #535E4E;
/* -moz-transition: box-shadow;
-moz-transition-duration: 1s; */
}

.post:hover {
box-shadow: 5px 6px 7px #455141;
/* -moz-transition: box-shadow;
-moz-transition-duration: 1s; */
}

.threadspam {
opacity: 0.1 !important;
height: 40px !important;
overflow: hidden;
}

.spam {
opacity: 0.1 !important;
height: 20px !important;
overflow: hidden;
}

.post, blockquote p {
font-family: Segoe UI;
}

.postername {
color: #D8DDD6 !important;
}

.postertrip {
color: #E7EAE6 !important;
}

.quote {
border-left-color: rgba(255,255,255,0.4) !important;
border-radius: 5px;
background: rgba(0,0,0,0.05) !important;
box-shadow: inset 3px 4px 8px rgba(0,0,0,0.2);
padding: 8px !important; 
/* -moz-transition: background;
-moz-transition-duration: 1s; */
}

.quote .quote {
font-size: 98%;
}

.quote:hover {
background: rgba(0,0,0,0.2) !important;
/* -moz-transition: background;
-moz-transition-duration: 1s; */
}

h1, h2 {
font-family: "Century Gothic";
font-weight: normal !important;
text-transform: lowercase;
color: #DADFD8 !important;
text-shadow: 0px 0px 9px #CAD2C7;
}

h1, h2 a {
text-shadow: 0px 0px 9px #DED785;
}

textarea, input, select, option {
-moz-appearance: none;
background: #B8C0B2 !important;
border: none !important;
color: #30352C !important;
font-family: Consolas;
box-shadow: 1px 2px 5px rgba(0,0,0,0.4);
border-radius: 6px;
padding: 3px;
}

.submit {
-moz-appearance: none !important;
background: rgba(0,0,0,0.12) !important;
color: #CAD2C7 !important;
padding-top: 3px !important;
padding-bottom: 3px !important;
-moz-transition: background, color, box-shadow;
-moz-transition-duration: 0.2s, 0.3s, 0.2s;
}

.submit:hover {
background: rgba(0,0,0,0.07) !important;
color: #FFFFFF !important;
-moz-transition: background, color, box-shadow;
-moz-transition-duration: 0.2s, 0.1s, 0.2s;
box-shadow: 1px 2px 5px rgba(0,0,0,0.6);
}

li.threadlink {
font-size: 80%;
}

a {
color: #DED785 !important;
text-decoration: none !important;
-moz-transition: color;
-moz-transition-duration: 0.2s;
}

a:hover {
color: #EBE79E !important;
text-decoration: underline !important;
-moz-transition: color;
-moz-transition-duration: 0.2s;
}

code, tt {
font-family: Consolas;
}

.navlinks a {
text-shadow: 2px 3px 3px rgba(0,0,0,0.2);
text-decoration: none;
}

/* syntax highlighting */

.str, .kwd, .com, .typ, .lit, .pun, .pln, .tag, .dec, .src, .atn, .atv {
color: white !important;
}

.str {
color: #B4C0B2 !important;
}

.com {
color: #9DAD9B !important;
}

.typ {
color: #D3DAD2 !important;
}

.kwd {
color: #BEC9BD !important;
}

.pun {
color: #B3C0B2 !important; 
}

.pln {
color: #E3E8E2!important;
}

}
