css:
/*****************************/
/* Hattrick XP blue          */
/* Date: 30/05/2011          */
/* Made by: Bjorn_Toulouse   */
/*****************************/


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hattrick.org") {

body{
background:url('http://lh4.googleusercontent.com/-eFEC3BA0Rvo/Td_DcT7LRmI/AAAAAAAAAJE/OdEgTqPjwiI/bg-xp-blue.png') !important;
}

a{
color:#224E7C !important;
}
a:hover{
color:#6F8DDC !important;
}



/*      Top menu       */

#top{
background:url(http://lh4.googleusercontent.com/-BziO_eGPEl0/Td31h7WjQgI/AAAAAAAAAGw/9g8LfxggAQg/master_page-blue.png) 0 0 no-repeat !important;
}

#header{
background:url(http://lh4.googleusercontent.com/-BziO_eGPEl0/Td31h7WjQgI/AAAAAAAAAGw/9g8LfxggAQg/master_page-blue.png) 0 -16px no-repeat !important;
}

#bottom{
background:url(http://lh4.googleusercontent.com/-BziO_eGPEl0/Td31h7WjQgI/AAAAAAAAAGw/9g8LfxggAQg/master_page-blue.png) 0 -155px no-repeat !important;
}

#menu a:hover{
background:#6F8DDC !important;
}

#menu a{
color:#fff !important;
}

#menu{
background:url('http://lh4.googleusercontent.com/-BziO_eGPEl0/Td31h7WjQgI/AAAAAAAAAGw/9g8LfxggAQg/master_page-blue.png') 0 -172px no-repeat !important;
}

#logo{
background:url(http://lh6.googleusercontent.com/-NZDvsj5LN-Y/Td_FIPl23qI/AAAAAAAAAJM/2mIvU-nnFgY/logo-xp-blue.png) top left no-repeat !important;
}



/*       Links & headers   */

h2 {
color: #000 !important;
}

h2 a{
color: #fff !important;
}

h3 {
color: #000 !important;
}



/*       Forum       */

.highlight{
color:#2F75F3 !important;
}

.folderName a{
color:#000 !important;
}

.forumTabs a{
color:#000 !important;
}

.cfUserInfo a{
color: #000 !important;
}

.cfHeader a {
color:#ffffff !important;
}

.cfWrapper{
border:solid 1px #224E7C !important;
}

.cfHeader{
background:#6F8DDC !important;
}



/*      Box backgrounds & text    */

.sidebarBox .boxHead h2,.subMenuBox .boxHead h2,.main .boxHead h2{
color:#fff !important;
}

.sidebarBox .boxHead,.subMenuBox .boxHead,.main .boxHead{
background:transparent url('http://lh4.googleusercontent.com/-BziO_eGPEl0/Td31h7WjQgI/AAAAAAAAAGw/9g8LfxggAQg/master_page-blue.png') no-repeat scroll right -207px !important;
}

.sidebarBox .boxHead .boxLeft,.subMenuBox .boxHead .boxLeft,.main .boxHead .boxLeft{
background:url('http://lh4.googleusercontent.com/-BziO_eGPEl0/Td31h7WjQgI/AAAAAAAAAGw/9g8LfxggAQg/master_page-blue.png') 0 -207px no-repeat !important;
}

.sidebarBox .boxFooter,.subMenuBox .boxFooter,.main .boxFooter{
background:url('http://lh4.googleusercontent.com/-BziO_eGPEl0/Td31h7WjQgI/AAAAAAAAAGw/9g8LfxggAQg/master_page-blue.png') no-repeat scroll right bottom !important;
}

.sidebarBox .boxFooter .boxLeft,.subMenuBox .boxFooter .boxLeft,.main .boxFooter .boxLeft{
background:url('http://lh4.googleusercontent.com/-BziO_eGPEl0/Td31h7WjQgI/AAAAAAAAAGw/9g8LfxggAQg/master_page-blue.png') no-repeat scroll left bottom !important;
}

.ft-new-mail, .ft-new-forum-msg {
color:#23FFED !important;
}



/* MISC */

.shy{
color:#8690A6 !important;
}


}
