css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ghacks.net") 

{

/*>>>Remove Content<<<*/

.head, .sidebar2, .sidebar3, .footer, .basement, .tags, .marginbottom, .socialize-in-content, .commentlist, #respond, .navigation, #comments, .wp-pagenavi span.pages, .searchbox, a[href="http://www.ghacks.net/ghacksnet-feed-list/"], a[href="http://www.ghacks.net/advertising-options/"], a[href="http://www.ghacks.net/contact/"], a[href="http://www.splitreason.com/ghacks/"], #mta_bar, .socialize-containter, #entry-author-info, #wibiyaToolbar, .main h1, .noscript-show
{
 display: none !important;
}




/*>>>Borders<<<*/

.article
{
 border: 2px solid #222 !important;
 -moz-box-shadow: 0 0 10px #444 !important;
 -moz-border-radius: 8px !important;
}

.extendspost li
{
 border: none !important;
}




/*>>>Backgrounds<<<

body, .wrapper, .wp-pagenavi a, .wp-pagenavi span.pages, .wp-pagenavi span.current
{
 background: #000 !important;
}
*/
.bottomnav 
{
 background: -moz-linear-gradient(#333, #000) !important;
 margin-bottom: 1px !important;
 height: 45px !important;
}

.bottomnav a:hover
{
 background: none !important;
}




/*>>>Positions<<<*/

.article
{
 position: relative;
 top: -55px !important;
}

.left-bl, .middle-bl, .right-bl
{
 position: relative;
 left: 175px !important;
}

.bottomnav li 
{
 position: relative;
 left: 135px !important;
}

.wp-pagenavi
{
 position: relative;
 top: -25px !important;
}








/*>>>Various<<<*/

.article
{
 margin-bottom: 15px !important;
}

.article h1
{
 margin-bottom: 0 !important;
}

.main
{
 width: 960px !important;
 padding: 10px !important;
}

.article h1
{
 padding: 7px 0 0 10px !important;
}

.content
{
 padding: 0 10px !important;
}

.small-blocks
{
 position: relative;
 top: -70px !important;
 background: black !important;
 width: 960px !important;
 margin-bottom: 0px !important;
}


.extendspost
{
 height: 20px !important;
 position: relative;
 top: -17px !important;
}

.bottomnav
{
 width: 980px !important;
 position: relative;
 right: 5px !important;
}

.middle-bl
{
 padding: 0 60px 0 60px !important;
}

.small-blocks h3
{
 margin-bottom: 5px !important;
}

a[href="http://www.ghacks.net/"]
{
 margin-left: 90px !important;
}

.alignnone
{
 display: block !important;
 margin-left: auto !important;
 margin-right: auto !important;
}

.article h1
{
 display: block !important;
 text-align: center !important;
}

}




/*<><><><><><><><><><><>><><><><><><><><><><><><><><><><><><><><><><><<><><><><><><><><><><><><><><><><><><><><><><><><><><><><><>*/




@-moz-document url-prefix("http://www.ghacks.net/page"), url-prefix("http://www.ghacks.net/category/windows"), url-prefix("http://www.ghacks.net/2010"), url-prefix("http://www.ghacks.net/category/browsing/firefox"), url-prefix("http://www.ghacks.net/category/operating-systems/linux"), url-prefix("http://www.ghacks.net/category/software"), url-prefix("http://www.ghacks.net/category/browsing/internet-explorer-browsing"), url-prefix("http://www.ghacks.net/category/email"), url-prefix("http://www.ghacks.net/archives"), url-prefix("http://www.ghacks.net/contact"), url-prefix("http://www.ghacks.net/advertising-options"), url-prefix("http://www.ghacks.net/ghacksnet-feed-list"), url-prefix("http://www.ghacks.net/2009"), url-prefix("http://www.ghacks.net/2008"), url-prefix("http://www.ghacks.net/2007"), url-prefix("http://www.ghacks.net/2006"), url-prefix("http://www.ghacks.net/2005"), url-prefix("http://www.ghacks.net/tag"), url-prefix("http://www.ghacks.net/2011")

{

.article, .wp-pagenavi
{
 position: relative;
 top: 0 !important;
}

.wp-pagenavi
{
 position: relative;
 top: 25px !important;
}

.main
{
 padding-bottom: 0 !important;
}

.article
{
 margin-bottom: 15px !important;
}

}




/*<><><><><><><><><><><>><><><><><><><><><><><><><><><><><><><><><><><<><><><><><><><><><><><><><><><><><><><><><><><><><><><><><>*/




@-moz-document url-prefix("http://www.ghacks.net/page")

{

.wp-pagenavi
{
 position: relative;
 top: 25px !important;
}

.main
{
 padding-bottom: 65px !important;
}

a[href="http://www.ghacks.net/"]
{
 margin-left: 0 !important;
}

.wp-pagenavi
{
 padding-bottom: 5px !important;
 position: relative;
 top: 35px !important;
}

.bottomnav li 
{
 position: relative;
 left: 227px !important;
}

}









@-moz-document url-prefix("http://www.ghacks.net/category")

{

div[class="article"] .extendspost
{
 display: none !important;
}

div[class="main"] h1
{
 color: orange !important;
 text-align: center !important;
}

div[class="article"] h1
{
 text-align: left !important;
}

.wp-pagenavi
{
 position: relative;
 top: 35px !important;
 padding-bottom: 70px !important;
}

}
