css:
@-moz-document 
url-prefix("https://calendar.google.com/")
{
/*顶部的导航条*/
#gb,#onegoogbar,.onegpad,

/*搜索条等*/
#vr-header,

/*左上角的“日历”二字*/
.applogo

/*左侧导航栏*//*有时需要加载任务列表，留着左边的导航栏会方便一点。*/
/*#nav*/

{display:none !important;}

/*顶部通知条*/
.mbox{margin-top:22px !important;}
.eui-t .mbox-cont { font-size:12px !important;}

/*显示日期时间的横栏*/
#vr-nav{padding: 0 0 2px 0 !important;margin: 2px 0 0 0 !important;height:30px !important;}

/*隐藏右侧的打印按钮和刷新按钮*/
#mg-print,#mg-refresh{display:none !important;}

/*“日、周、月、5天、日程”右对齐*/
.button-strip{right:0 !important;}

/*“今天”按钮左对齐*/
#topLeftNavigation{position:static !important}

/*调整左侧导航栏距离左侧的空间，使之更加紧凑*/
#nav {margin-left: 16px !important;}

/*“创建”按钮微调*/
.qnb-container .goog-imageless-button{width: 120px !important;font-size:14px !important;}

/*调整日历主体区域的现实面积*/
#mothertable{padding-top:6px !important;margin-left:0 !important;}
#mainbody {margin-left: 170px !important;padding-top: 0!important;}

/*页面主体右侧空白*/
.eui-t #gridcontainer {margin-right: 0 !important;}
}
