css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("forums.officialnintendomagazine.co.uk") {

.ads_leaderboard,
.ads_skyscraper,
.ads_takeover,
.ads_mpu,
.ads_mpu_header,
.vid_overlay,
img[src="/templates/images/recruit_button_160.gif"],
.wrap_footer {
display: none !important;
}

* { color: black !important}
a:link { color: #006699 !important }
a:visited { color: DarkSlateBlue !important } 
a:visited:hover { color: #333333 !important } 
a:visited:hover { text-decoration: underline !important } 
a:hover { text-decoration: none !important }
a:active { color: red !important }
input[type=button], input[type=submit], input[type=reset]  { background-color: ButtonFace !important; color: ButtonText !important; border-color: ButtonHighlight ButtonShadow ButtonShadow ButtonHighlight !important }
input[type=text], input[type=password], input[type=file] { background-color: Window !important; color: WindowText !important }


.code { 
   font-family: Courier, 'Courier New', sans-serif !important; font-size: 11px !important; color: #444444 !important; 
   background-color: #efefef; border: #a7b0b8; border-style: solid; 
   border-left-width: 1px; border-top-width: 1px; border-right-width: 1px; border-bottom-width: 1px 
} 

.quote { 
   font-family: Verdana, Arial, Helvetica, sans-serif !important; font-size: 11px !important; color: #444444 !important; line-height: 

125%; 
   background-color: #efefef !important; border: #a7b0b8 !important; border-style: solid !important; 
   border-left-width: 1px !important; border-top-width: 1px !important; border-right-width: 1px !important; border-bottom-width: 1px !important 
} 

/* General font families for common tags */ 
hr   { height: 0px!important; border: solid #00526B 0px !important; border-top-width: 1px !important;} 

/* This is the border line & background colour round the entire page */ 
.bodyline   { background-color: #FFFFFF !important; border: 2px !important; } 

/* This is the outline round the main forum tables */ 
.forumline   { background-color: #FFFFFF !important; border: 2px !important; } 

/* Main table cell colours and backgrounds */ 
td.row1   { background-color: #EFEFEF !important; font-family: Verdana, Arial, Helvetica, sans-serif !important; } 
td.row2   { background-color: #DEE3E7 !important; font-family: Verdana, Arial, Helvetica, sans-serif !important; } 
td.row3   { background-color: #D1D7DC !important; font-family: Verdana, Arial, Helvetica, sans-serif !important; } 


/* 
  This is for the table cell above the Topics, Post & Last posts on the index.php page 
  By default this is the fading out gradiated silver background. 
  However, you could replace this with a bitmap specific for each forum 
*/ 
td.rowpic { 
      background-color: #FFFFFF !important; 
      background-repeat: repeat-y !important; 
} 

td.spaceRow {
display: none !important; 
 
} 

td.catBottom { background-color: #006699 !important; font-family: Verdana, Arial, Helvetica, sans-serif !important; } 

td.cat,td.catHead,td.catSides,td.catLeft,td.catRight {
display: none !important; 

}

/* Header cells - the blue and silver gradient backgrounds */ 
th   { 
   color: #FFA34F !important; font-size: 12px !important; font-weight : bold !important; 
   background-color: #006699 !important; height: 25px !important; 
} 



.wrap_page {
background: #fff !important;
width: 100% !important;
padding: 0px !important;
}

.wrap_content {
position: absolute !important;
top: 0px !important;
left: 0px !important;
width: 100% !important;
height: 100% !important;
margin-top: 0px !important;
margin-left: 0px !important;
margin-right: 4px !important;
}

.wrap_sidebar {
display: none !important;
}

.wrap_sidebar * {
display: none !important;
}

.wrap_forums {
width: 100% !important;
height: 100% !important;
position: relative !important;
top: 0px !important;
left: 0px !important;
}

.wrap_topbox {
display: none !important;
}

.wrap_forums_post {
width: 100% !important;
}

.pod_light_header_full {
padding-left: 0px !important;
background-repeat: no-repeat !important;
background-color: #fff !important;
width: 100% !important;
font-size: 0px !important;
}

.pod_light_header_full:before {
content: " " !important;
height: 4px !important;
width: 100% !important;
background-color: #7B7B8C !important;
display: block !important;
}

.wrap_forums td[align="left"][valign="top"][width="110"] {
width: 0% !important;
}
.wrap_forums td[valign="top"][width="100%"] {
width: 100% !important;
}
.genmed {color: #ffffff;}

/*****************************************
* Bastard Video Player and Kudos Rubbish *
*****************************************/
table[background="/templates/images/vidthumb2.gif"],
.wrap_sidebar .wrap_pod_more,
.wrap_sidebar .vid_overlay/*,
.pod_dark*/ {
display: none !important;
}
/*.pod_dark .pod_dark_header .pod_header,*/
.pod_dark .pod_dark_header .vid_overlay,
.pod_dark .wrap_pod a img {
display: none !important;
}
}
