css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
domain("fark.com"),
domain("foobies.com")
{
 body { /*[[bodybg]]*/ }
 hr { visibility: hidden !important; }
 footer { /*[[bodybg]]*/ }

 #header { height: 30px !important; }
 #logo { height: 0px !important; display: none !important; }
 #headerBottom { margin-top: -105px !important; }
 #tagLine { font-size:10pt !important; /*[[showtagline]]*/ }
 #userName { font-size: 10pt !important; }
 #headerMenuContainer { height: 25px !important; }
 #headerMenu { font-size: 10pt !important; margin-top: -5px !important; }
 #catMenu { font-size: 10pt !important; margin-top: -15px !important; }
 #topAd728x90 { display: none !important; height: 0px !important; }
 #rightAd300x250 { display: none !important; }
 #rightAd300x250Lower { display: none !important; }
 #rightColSpacer { width: 150px !important; }
 #rightCol { width: 150px !important; }
 #rightCol>a:nth-child(1) { height: 9px !important; }
 #gplus { display: none !important; }
 #facebook { display: none !important; }
 #twitter { display: none !important; }
 #rss { display: none !important; }
 #hotw { margin-top: -10px !important; }
 #hotw a { font-size: 10pt !important; }
 #boxSwap { display: none !important; }
 #footerFarkLinks { display: none !important; }
 #alsoOnFarkTable { display: none !important; }

 .menuSelected a { font-size: 10pt !important; }
 .catSelected a { font-size: 10pt !important; }
 .submitLink { font-size: 10pt !important; }
 .mainDate { font-size: 10pt !important; margin-top: -10px !important; }
 .headlineRow { font-size: 10pt !important; }
 .headlineRow td { padding: 3px 3px !important; }
 .headlineSourceImage { width: 110px !important; }
 .sidebarLinks { border: #000000 1px solid !important; background-color: #696969 !important; padding: 5px !important; margin-top: -10px !important; }
 .sidebarLinks li { width:100% !important; text-align: center !important; font-size: 10pt !important; }
 .alsoOnFark { display: none !important; }
 .shoprotator { display: none !important; }
}
