css:
/* Userstyle.org URL: "http://userstyles.org/styles/41152" */



@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("facebook.com") {



body.ego_page, body.profile, body.frame_wide, body.hasLeftCol, body.editaccount, body.fbx, body.WelcomePage {

background-image: url("http://30.media.tumblr.com/tumblr_lm3pxxgjkZ1qh7goko1_500.gif") !important;

background-color: #333333 !important;

background-attachment: fixed !important;

}



#pageHead {

background-color: #3B5998 !important;

}



#globalContainer {

background-color: white !important;

width: 980px !important;

}



.WelcomePage #globalContainer {

margin-left: 15% !important;

}



#contentCurve {

padding-top: 10px !important;

}



.ego_page #contentCol, .ego_page #contentCurve, .HelpCenter #contentCol, .HelpCenter #contentCurve, .hasLeftCol #contentCol, .hasLeftCol #contentCurve, .fbx #contentCol, .fbx #contentCurve, .uiBoxWhite {

background-color: whitesmoke !important;

}



#contentArea .uiComposerTopBorder {

padding: 0 8px 5px 8px !important;

border: 1px solid #d8dfea !important;

background-color: #EDEFF4 !important;

}



.uiComposerTopBorder .uiComposerMessageBox {

background-color: #EDEFF4 !important;

}



.uiComposerTopBorder .mutableContent {

background-color: white !important;

}



.editaccount #contentCurve, .frame_wide #contentCurve {

background-color: white !important;

}



.careers .mvl {

margin-left: -10px !important;

width: 930px !important;

}



.profile .ginormousProfileName {

font-family: Georgia !important;

}



.profile .profile-friends img {

width: 25px !important;

height: 25px !important;

}



.profile #pagelet_photo_bar {

margin-left: 3px !important;

}



.profile #pagelet_photo_bar li i {

border: 1px solid white !important;

width: 70px !important;

margin: 1px 1px 1px 0px !important;

}



.profile #pagelet_photo_bar li {

border: 1px solid #D8DFEA !important;

margin: 0px 2px 0px 2px !important;

}



.ego_page .confirm, .ego_page .confirm_boxes {

background-color: whitesmoke !important;

}



.ego_page.hasLeftCol .UIImageBlock.fbxWelcomeBox {

margin-left: 5px !important;

}



.ego_page.hasLeftCol #contentArea {

margin-left: 0px !important;

}



.ego_page #contentArea {

margin-left: 10px !important;

}



}
