css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fark.com") {
	.headlineCommentsRej, .headlineCommentsRej a, .headlinerej, .headlinerej a {
		color:#800000 !important;
		font-size:10pt !important;
		background:#F5EBEB !important;
	}
	.headlinelive, .headlinelive a {
		color:#006000 !important;
		font-size:10pt !important;
		font-size:9pt !important;
	}
	.headlineComments {
		width:120px !important;
		padding:5px !important;
		text-align:center !important;
		vertical-align:middle !important;
	}
	.headlineRow td{
		font-size:10pt !important;
		font-family:Verdana,Arial,Helvetica,sans-serif !important;
		padding:3px !important;
	}
	.headlineRow1 td{
		font-size:10pt !important;
		font-family:Verdana,Arial,Helvetica,sans-serif !important;
		padding:3px !important;
	}
	body {
		background:#777 !important;
		background-image:none !important;
	}
	.ctable td, .ctableTF td {
		vertical-align:middle !important;
		height:26px !important;
	}
	table tr td.headlineTopic{
		padding:4px
	}
	.clogin a {
		font-size:0.9em !important;
	}
	select,input,textarea,button{
		font:9pt sans-serif
	}
	#commentsArea {
		width:99% !important;
		font-size:0.9em !important;
		font-family:Verdana,Arial,Helvetica,sans-serif !important;
	}
	#commentsArea .ctext {
		margin-bottom:25px !important;
	}
	#catMenu {
		margin-top:8px !important;
		height:30px !important;
	}
	#catMenu li{
		margin-right:1px !important;
		line-height:30px !important;
	}
	#catMenu li.catSelected a {
		font-size:0.9em !important;
		background-color: #437 !important;
		color:#fff !important;
		border:2px solid #3d3d3d !important;
		border-bottom:0px !important;
		margin-top:3px !important;
		-moz-box-shadow:2px 2px 4px #333 !important;
		-webkit-box-shadow:2px 2px 4px #333 !important;
		box-shadow:2px 2px 4px #333 !important;
	}
	#catMenu li a {
		color:#fff !important;
		font-size:0.8em !important;
		background-color: #669 !important;
		border:2px solid #3d3d3d !important;
		border-bottom:0px !important;
		-moz-box-shadow:2px 2px 4px #333 !important;
		-webkit-box-shadow:2px 2px 4px #333 !important;
		box-shadow:2px 2px 4px #333 !important;
	}
	#newsContainer{
		border:2px solid #3d3d3d !important;
	}
	#leftCol{
		width:95% !important;
		float:left !important;
	}
	#leftColSpacer{
		width:0% !important;
	}
	#rightColSpacer{
		width:0% !important;
	}
	.alsoOnFark{
		display:none !important;
	}
	footer{
		display:block !important;
		float:left !important;
		margin-right:0px !important;
	}
	.headlineRow td{
		padding:4px !important;
		font-size:9pt !important;
	}
	.headlineRow1 td{
		padding:4px !important;
		font-size:9pt !important;
	}
	.mainDate{
		text-align:center !important;
		font-weight:bold !important;
		font-size:14pt !important;
		padding:0 0 10px 10px !important;
	}
	#commentsPostingArea .commentsPostingAreaInput{
		font-size:10pt !important;
		font-family:Verdana,Arial,Helvetica,sans-serif !important;
		color:#000 !important;
	}
	#commentsPostingArea .commentPostingTable td{
		padding:2px !important;
	}
	#jsblobbo1,#bodyRightSideContainer, #topAd728x90,#footer,#rightSideLeftMenubar,#rightCol,#alsoOnFarkTable,#boxSwap,#rightAd300x250Lower {
		display:none !important;
	}
	.headlineCommentsTags {
		display:none !important;
	}
        .profhdr1 {
                 width:15% !important;
        }
}
