css:
@-moz-document domain('tumblr.com'), url-prefix('http://www.tumblr.com/') , url-prefix("https://www.tumblr.com/") {



#dashboard_index div.l-content,#dashboard_submissions div.l-content,#dashboard_drafts div.l-content,#dashboard_post_queue div.l-content,#dashboard_likes div.l-content,#main_tag_beta #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

background: rgba(0,0,0,0.2) !important;

}

#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue,#dashboard_likes, #dashboard_followers, #dashboard_following, #dashboard_members, #main_tag_beta, #dashboard_upload_static_file, #main_about, #main_doc_page, #main_themes, #main_meetups, #main_jobs, #main_directory_page, #dashboard_settings, #dashboard_goodies {

background-image:none !important;

background-repeat:repeat-x !important;

background-attachment:fixed !important;

background-color:#222222 !important;

background-position:top

}





#dashboard_index{background:#2A2D30 repeat-x 50% 0% !important;}







#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:#000000!important;}







ol#posts li.notification{background-color: #000000 !important;



border-bottom: 1px solid #2F465C !important;



color: #A8BCCB !important;}







#left_column .dashboard_options_form {



   background: url('http://assets.tumblr.com/images/white_20.png')#000000;



}







#right_column a#dashboard_switch_blog_menu_current:hover {



   background: transparent;



}







#nav .nav_item.active{



   background-color:#353738



}







#left_column #posts .post .permalink{



   background-color:#2A2D30;



}







#nav .nav_item.active, #nav .nav_item .nav_menu, #nav .nav_item .nav_item_nipple .nav_item_nipple_pixel{



   background-color:#353738;



}







}
