css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("joyreactor.ru") {

body {background: #2E3436 !important; font-family: Trebuchet MS,sans-serif !important; color: #BABDB6 !important;} 
#container, #tagList, #searchBar, #navlist, #contentinner {background: none !important;}
#footer {display: none;}
#header {background: url('http://img813.imageshack.us/img813/313/logoxd.png') no-repeat; height: 95px !important;}
#header .logo, .description{visibility: hidden !important;}
#navcontainer ul li.current_page_item a, ul li.current_page_ancestor a, ul li a{color: #E9B96E !important; font-size: 1.1em !important; font-weight: bold !important; background: none !important; font-family: Trebuchet MS,sans-serif !important; text-decoration: underline !important;}
#submenu a {color: #FCAF3E !important; font-size: 0.9em !important;}
.post-normal, .post-good, .post-bad {border: 0 !important; background: #272C2D !important; border-radius: 1em 1em 1em 1em; padding: 7px !important;}
#contentinner form div b label {color: #BABDB6 !important;}
a {color: #729FCF !important;}
#searchBar {padding-left: 30px !important;}
.podst_rating span a img {visibility: hidden !important;}
.commentnum {background: url("http://img541.imageshack.us/img541/9796/commenth.png") no-repeat scroll left center transparent !important;}
.comment {background: none !important;}
.avatar img{visibility: hidden !important;}
.avatar {background: url("http://img541.imageshack.us/img541/9796/commenth.png") no-repeat scroll left center transparent !important;}
div.mood {background: #FFFFFF !important; height: 60px !important; width: 170px !important; display: block !important;}
a.online_username {color: #79CC79 !important;}
div.post-normal div div.uhead_nick img {visibility: hidden !important;}
div.post-normal div div.uhead_nick {background: url("http://img90.imageshack.us/img90/2757/smilenormal32.png") no-repeat !important}
div.post-good div div.uhead_nick img {visibility: hidden !important;}
div.post-good div div.uhead_nick {background: url("http://img832.imageshack.us/img832/800/smilegood32.png") no-repeat !important}
div.post-bad div div.uhead_nick img {visibility: hidden !important;}
div.post-bad div div.uhead_nick {background: url("http://img8.imageshack.us/img8/5866/smilebad32.png") no-repeat !important}
#sidebar ul li {background: #272C2D !important; border-radius: 1em 1em 1em 1em !important; color: #BABDB6 !important}
#sidebar ul li div.sideheader {background: none !important; color: #BABDB6 !important}
.pagination .current {color: #2E3436 !important;}
}
