css:
@namespace url(http://www.w3.org/1999/xhtml);
@font-face {
  font-family: 'Oswald';
  font-style: normal;
  font-weight: normal;
  src: local('Oswald '), local('Oswald'), url('http://themes.googleusercontent.com/font?kit=-g5pDUSRgvxvOl5u-a_WHw') format('woff');
}
@font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 400;
  src: local('Open Sans'), local('OpenSans'), url('http://themes.googleusercontent.com/font?kit=cJZKeOuBrn4kERxqtaUH3T8E0i7KZn-EPnyo3HZu7kw') format('woff');
}
@-moz-document domain("vandalweb.uidaho.edu") {
	* {font-family: 'Open Sans', arial, serif !important; }
	body { margin:0 !important; padding:0 !important; width:100% !important; background: url(data:image/png;base64,) top center repeat-x;font-family: 'Open Sans', arial, serif; }
	.headerwrapperdiv, .pagetitlediv, .pagebodydiv, .powerdbydiv, .pagefooterdiv {width:75%; margin:0 auto !important; background-color:#FFF; padding:10px;}
	.poweredbydiv {display:none ; }
	.headerwrapperdiv { margin-top:10px !important; border-top-right-radius:10px; -webkit-border-top-right-radius:10px; border-top-left-radius:10px; -webkit-border-top-left-radius:10px;}
	.pageheaderdiv1 h1 {font-family: 'Oswald', arial, serif !important; font-size:25pt !important; }
	.pagefooterdiv { border-bottom-right-radius:10px; -webkit-border-bottom-right-radius:10px; border-bottom-left-radius:10px; -webkit-border-bottom-left-radius:10px; }
	td.mpdefault { padding:5px; }
	td.mpdefault:nth-child(2) { border:1px dotted #CCC;}
	td.mpdefault:nth-child(2):hover { background-color:#EEE; }
	
	.pagebodydiv a:link, .pageheaderlinks a:link { color:#5c8eb6 !important; }
	.pagebodydiv a:visited, .pageheaderlinks a:visited  { color:#5c8eb6 !important; }
	.pagebodydiv a:active, .pageheaderlinks a:active  { color:#5c8eb6 !important; }
	.pagebodydiv a:hover, .pageheaderlinks a:hover  { color:#178fcd !important; }
	
	.bgtabon, .bgtaboff { display:none;}
	.taboff { background:#7eb6d3 !important; border-right:5px solid white; border-left:5px solid white; padding:5px !important; border-radius:5px ; }
	.tabon { background:#1297dc !important; border-right:5px solid white; border-left:5px solid white; padding:5px !important; border-radius:5px ; }
	.tabon a:link {color:#FFF !important; }
	
	.taboff a:hover, .tabon a:hover { text-decoration:underline !important; }
}
