css:
@-moz-document 
/* http://www.blogger-fa.com/ */
/* symoniri (symoniri@gmail.com OR blogger.fa@gmail.com) */

url-prefix(http://www.blogger.com/),
url-prefix(http://www.blogger.com/home),
url-prefix(http://www.blogger.com/profile),
url-prefix(http://draft.blogger.com/home),
url-prefix(http://draft.blogger.com/profile),
url-prefix(http://www.blogger.com/features),
url-prefix(http://draft.blogger.com/features),
url-prefix(http://www.blogger.com/upload-image.g),
url-prefix(http://www.blogger.com/choose-gadget) {

span.flag-text, div#b-search-this {
font:normal 8pt Tahoma !important;
}

input#b-query {
font-weight:normal !important;
font-family: Tahoma !important;
padding:1px 2px !important;
}

#global-info {
font: bold 8pt Tahoma !important;
}

#global-info a:hover {
color: #fff !important;
}

#header div#blogname, div.blogTitleContainer div.blogTitle, #blogsOfNote-link, .topright a, #buzz-link, span.alreadyAdded {
font: bold 11pt Helvetiga, Arial !important;
}

#blogname h1, #blogname h1 a {
font: bold 22pt B Nazanin, Helvetiga, Arial !important;
}

.innerright .links, .blogLastPublished, .cssButtonInner, .showHideContainer, .profileLinksContainer, div#moreContent, .mid ul, div.mid, span.description p, span.description {
font: normal 8pt Tahoma !important;
line-height: 1.5em !important;
}

div#moreContent {
text-align: right !important;
font: normal 8pt Tahoma !important;
}

.cssButtonInner {
padding: 2px;
color: #fff !important;
}

a, a:hover, a:visited {
text-decoration: none !important;
font-family: Tahoma;
font-weight: normal !important;
}

.profilePhoto img, .profilePhotoContainer img {
border: 0px !important;
}

div.desc, #body textarea {
font: normal 9pt Tahoma !important;
}

#body .errorbox-good {
font: normal 9pt Tahoma !important;
line-height: 1.6em !important;
}

#body p {
font: normal .9em Tahoma !important;
line-height: 1.6em !important;
}

.table-header {
font: normal 12pt Helvetiga, Arial, Times New Roman !important;
}

.info-text p {
text-align: right !important;
font: normal 9pt Tahoma !important;
line-height: 1.6em !important;
margin: 0 !important;
}

#tabnav {
font: normal 11pt Helvetiga, Arial, Times New Roman !important;
}

#subnav {
font: normal 9pt Tahoma !important;
padding: 0 2px !important;
}

label {
font: bold 13pt Helvetiga, Arial, Times New Roman !important;
margin: 5px 0 !important;
line-height: 1.6em !important;
}

.row-set-descriptor {
margin: 5px 0 !important;
font: bold 13pt Helvetiga, Arial, Times New Roman !important;
}

.headerSwitchLink {
font: normal 10pt Tahoma !important;
}

#footer {
font: normal .95em Tahoma !important;
line-height: 1.6em !important;
}

a:link {
text-decoration: none !important;
}

select {
font: normal 8pt Tahoma !important;
padding: 1px 2px !important;
}

#manageBlogsBox .links {
font: bold 9pt Tahoma !important;
}

.blogEntry .title {
font: bold 9pt Tahoma !important;
}

.blogEntry .postInfo {
font: normal 9pt Tahoma !important;
}

.blogEntry .links {
font: normal 10pt Tahoma !important;
}

#sidebar .mod3, span#save-message-inner, #app-outer-wrap p {
font: normal 8pt Tahoma !important;
line-height: 1.6em !important;
}

#sidebar .mod3 h2 {
font: bold 13pt Helvetiga, Arial, Times New Roman !important;
line-height: 1.6em !important;
}

#moreBuzz {
font: bold 13pt Helvetiga, Arial, Times New Roman !important;
text-align: right !important;
}

#mobile-devices #learn {
font-size: 9pt !important;
}

.mod3 p img, #profile-mod img {
border: 0 !important;
}

.innerleft h4, .innerright h4, .topright h4 {
font: bold 11pt Helvetiga, Arial, Times New Roman !important;
}

.headerLeft h2 {
font: bold 13pt Helvetiga, Arial, Times New Roman !important;
}

.box .topright {
_height:30px !important;
}

/* Blogger.com/features page */

#m3 h1 {
font: bold 13pt Helvetiga, Verdana, Arial !important;
}

div.featuresPublish, div.featuresSimple, div.featuresUrl, div.featuresTemplate, div.featuresTemplate, div.featuresPhotos, div.featuresFeedback, div.featuresSubscribePost, div.featuresId, div.featuresLanguages, div.featuresMobile, div.featuresTeamBlog, div.featuresThirdParty, div.featuresMore {
font: normal 9pt Tahoma !important;
line-height: 1.6em !important;
}

/* www.blogger.com/upload-image */

#add-from-disk .note, #image-layout-positions ul li, #uploader #quotaMessage, div#image-layout-sizes #image-layout-remember {
font: normal 8pt Tahoma !important;
}

div#image-layout-sizes strong {
margin-right: 10px !important;
font: bold 13pt Helvetiga, Verdana, Arial !important;
}

#m3 h4, #m3 h2, .layoutEditorTitle h2 {
font: bold 13pt Helvetiga, Verdana, Arial !important;
}

.layoutEditorTitle {
font: normal 8pt Tahoma !important;
}

.widget-wrap3 .widget-content .layout-title {
font: bold 8pt Tahoma !important;
text-align: right !important;
text-indent: 10px;
}

.widget-wrap3 .widget-content .editlink {
font: normal 8pt Tahoma !important;
}

.add_widget a {
font: bold 9pt Tahoma !important;
}

#tabnav {
font:bold 9pt Tahoma !important;
}
}

@-moz-document url-prefix(http://www.blogger.com/publish-confirmation.g) {

#promo {
font: normal 8pt Tahoma !important;
line-height: 1.6em !important;
}
.main h1 {
font: bold 13pt Verdana, Helvetiga, Arial !important;
}
h1, h1 span {
font: normal 12pt Verdana, Helvetiga, Arial !important;
}
}

@-moz-document url-prefix(http://www.blogger.com/create-blog.g),
url-prefix(http://www.blogger.com/choose-template-new.g),
url-prefix(http://www.blogger.com/signup-done.g) {

#reg-done h1 {
font: bold 13pt Helvetiga, Arial, Times New Roman !important;
margin: 0 0 5px 0 !important;
padding: 5px 60px 0 0 !important;
}
#progress, .desc, .desc2, .preview, #reg-done p {
font: normal .9em Tahoma !important;
line-height: 1.6em !important;
}
}

@-moz-document url-prefix(http://www.blogger.com/blog-publishing.g) {
#status-msg-inner {
font: normal 9pt Tahoma !important;
}
#publishtype h1{
font: bold 13pt Helvetiga, Arial, Times New Roman !important;
margin: 0px !important;
}
#publishtype p{
font: normal 10pt Tahoma !important;
margin: 5px 0 !important;
line-height: 1.6em !important;
}
}

@-moz-document 
url-prefix(http://www.blogger.com/post-create.g),
url-prefix(http://www.blogger.com/post-edit.g),
url-prefix(http://www.blogger.com/posts.g),
url-prefix(http://www.blogger.com/bulk-post-edit.do) {

#richeditorframe {
font: normal 9pt Tahoma !important;
padding: 5px !important;
}
#autosaveStatus, #button-wrap, #editarea, #formatbar, #modebar, .labels-title, .selected-label li {
font: normal .95em Tahoma !important;
}
#postButtons, .pagination {
font-size: 13pt !important;
}
#fontTipMessage P {
margin-right: 5px !important;
}
.postsTable, #posts .posts, .entirePost {
font: normal 9pt Tahoma !important;
}
.postContents, .post-labels {
font: normal 9pt Tahoma !important;
text-align: justify !important;
line-height: 16pt !important
}
.selected-label, .labels-title, #labels-wrap {
font: bold .9em Tahoma !important;
margin-bottom: 5px !important;
line-height: 15pt !important;
}
input#f-title {
font: bold 13pt Times New Roamn !important;
padding:1px 5px !important;
}
#modebar span#ShowSourceEditor, #modebar span#ShowRichEditor {
float: left !important;
}
}

@-moz-document 
url-prefix("http://www.blogger.com/comment-iframe.g"),
url-prefix(https://www.blogger.com/comment-iframe.g) {

#commentBodyField {
margin: 2px !important;
}

textarea#commentBodyField, textarea {
padding: 2px !important; 
font: normal 9pt Tahoma !important;
line-height: 1.6em !important;

}

#identityControlsHolder {
margin-right: 5px !important;
}

span#signoutLink, #subscribeButtonHolder a#subscribeButton {
font: normal 9pt Tahoma !important;
}

input#postCommentSubmit, input#postCommentPreview {
font: normal 9pt Tahoma !important;
padding: 3px 1px !important;
}

}
