@-moz-document domain("facebook.com") {
#headNav {
background-color: /*[[color]]*/ !important;
border: 1px solid #000 !important;
margin-left: 180px !important;
}

#navAccount ul a:hover, #navAccount ul a:focus, #navAccount ul a:active, #navAccount .logoutButton:hover input, #navAccount .logoutButton input:active, #navAccount .logoutButton input:focus {
background: rgba(0,0,0,.7) !important;
border-bottom: 1px solid #f00 !important;
border-top: 1px solid #f00 !important;
padding: 3px 10px 4px !important;
text-decoration: none !important;
}

#navAccount ul a, #navAccount ul .logoutButton input {
color: #f00 !important;
}

#navAccount ul a:hover, #navAccount ul .logoutButton input:hover {
color: #fff !important;
}

#navAccount ul {
background: rgba(255, 255, 255, .6) !important;
}

#navAccountInfo #navAccountPic {
background: none !important;
border-bottom: none !important;
border-top: none !important;
border-color:red !important;
padding: 3px 10px 4px !important;
}

#navAccount #navAccountName {
background: none !important;
border-bottom: none !important;
border-top: none !important;
padding: 0 !important;
color:#f00 !important;
}
#navAccount #navAccountName:hover{
font-weight:bold !important;
color:#000 !important;
}

#navAccountInfo:hover #navAccountPic:hover img:hover{
border:1px solid #000 !important;
}

#pageNav a:hover,#pageNav a:focus,#pageNav a:active{
background-color:#000 !important;
}
.openToggler #navAccountLink, .no_js #navAccount:hover #navAccountLink,.openToggler #navAccountLink:hover {
background-color:#fff !important;
}

