css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("primo-test.bib.umontreal.ca"), domain("primo.bib.umontreal.ca") {

body
{
    background: url("http://www.umontreal.ca/enseigner/images/iu/fond-bodypng.png") repeat-x scroll 0 89px transparent !important;
    min-width: 0 !important;
    font-family:Verdana,Arial,Helvetica,sans-serif !important;
    color: #333333 !important;
    font-size:90% !important;
}

#exlidUserAreaTile
{
    float: none;
    position: absolute;
    right: 65px;
    top: 125px;
}

#exlidUserAreaRibbon li
{
/*    background-image: none !important;*/
}

#exlidUserAreaRibbon li span.EXLUserAreaStart {
/*    display: none;*/
}

#exlidUserAreaRibbon li.EXLLastItem {
    padding-right: 0 !important;
}


#contentEXL,
#content
{
    width: 966px !important;
    margin: 0 auto !important;
    position: relative;
}

/* en-tête */

#exlidHeaderContainer {
}

#exlidHeaderTile {
    background: url("http://www.bib.umontreal.ca/images/modele/test-bandeau-UdeM1.png") no-repeat scroll 0 0 transparent !important;
    height: 121px;
    width: 100% !important;
    padding: 0 !important;
    margin: 0 !important;
}

#exlidMainMenuTile {
    margin-top: 1.8em !important;
    width: 100% !important;
}

#exlidMainMenuTile div.EXLMainMenuContainer {
    float: left!important;
    width: 100% !important;
    margin-bottom: 5px !important;
}

#exlidHeaderTile a {
    display: block;
    height: 48px;
    width: 245px;
    position: absolute;
    left: 200px;
    top: 33px;
}

#exlidHeaderTile a img{
    display: none;
}

#exlidSearchTile
{
    clear: both !important;
}

#exlidSearchBanner {
    display: none;
}

#exlidHomeContainer {
    width: 100% !important;
}

#exlidHomeContainer div.EXLHomeBlockContainer1 div.EXLContentBlock1,
#exlidHomeContainer div.EXLHomeBlockContainer1 div.EXLContentBlock2 {
    width: 46% !important;
}

#exlidHomeContainer div.EXLHomeBlockContainer1 div.EXLContentBlock1 div.EXLHomeQuickLinksContainer {
    margin-left: 2em !important;
}

.EXLLanguagesLabel
{
    display: none;
}

#exlidMainMenuRibbon {
    float: left !important;
    padding-right: 0.2em;
    padding-top: 0.2em;
}

#exlidMainMenuTile div.EXLLanguagesContainer {
    float: none !important;
    left: auto !important;
    right: 0 !important;
    top: 120px;
    position: absolute !important;
    width: 6em !important;
    background-image: url("http://primo-test.bib.umontreal.ca:1701/primo_library/libweb/css/../images/bg_strip_subNav.png");
    background-repeat: repeat-x;
}

#exlidMainMenuRibbon li,
#exlidMainMenuTile div.EXLLanguagesContainer
{
    font-size:70% !important;
}

.EXLSearchFieldRibbonFormFields {
    width: 82% !important;
}

.EXLSearchFieldRibbonFormSubmitSearch {
    width: 18% !important;
}

.submit {
    background: url("http://www.bib.umontreal.ca/modele/2.3/images/iu/bouton-recherche.png") no-repeat scroll 100% top transparent !important;
    border: medium none;
    box-shadow: 0 0 3px #888888;
    color: #FFFFFF;
    float: none;
    font-size: 0.8em !important;
    height: 21px !important;
    line-height: 17px !important;
    margin: 0 !important;
    padding: 2px 2.2em 5px 0.5em !important;
    position: relative;
    text-transform: uppercase;
    width: auto !important;
    display: inline-block;
}

.EXLCustomLayoutContainer a:link {
    color: #003090 !important;
    text-decoration: underline !important;
}

.EXLCustomLayoutContainer a:visited {
    color: #551A90 !important;
    text-decoration: underline !important;
}

.EXLFacetList a:link,
.EXLFacetList a:visited {
    text-decoration: none !important;
}

#footerEXL
{
    font-size: 70%!important;
}

#contentEXL:after {
    background: url("http://www.bib.umontreal.ca/images/modele/test-pied-page-UdeM.png") no-repeat scroll center 0 #8e8e8e !important;
    border: 0 !important;
    height: 284px;
    width: 100%;
    position: absolute;
    overflow: hidden;
    content: "";
    display: block;

}





}
