@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("thepiratebay.org") {

/* General Styles */
body {
background:#3c3c3c !important;
color:#c8c8c8 !important;
}

#q {
padding-bottom:10px !important;
}

h2 {
background:none !important;
color:#fff !important;
border:0 !important;
}

h1 a {
background:url(data:image/png;base64,) no-repeat !important;
width:500px !important;
height:200px !important;
}

#detailsframe, #comments p.byline, #comments .comment, #details dt, #details, #detailsframe #title {
background:#3c3c3c !important;
color:#c8c8c8 !important;
border:0 !important;
}
#details h4 {
color:#fff !important;
}
.download a {
background:none !important;
padding:0 !important;
}
#details dd, #details dl.col2 dd {
text-decoration:none !important;
border:0 !important;
}
.nfo {
background:#2d2d2d !important;
border:1px solid #646464 !important;
}
.spons-link {
display:none !important;
}



/* Images */

img[src*="/img/tpblogo_sm_ny.gif"] {
margin-top:0 !important;
width: 0 !important;
height: 87px !important;
padding-left:87px !important;
background:url(data:image/png;base64,%3D%3D) no-repeat !important;
}

img[src*="/img/tpb_k.gif"], img[src*="/img//dl.gif"] {
display:none;
}

img[src*="/img/next.gif"] {
margin-top:0 !important;
width: 0 !important;
height: 12px !important;
padding-right:11px !important;
background:url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAsAAAAMCAYAAAC0qUeeAAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAAN1wAADdcBQiibeAAAABl0RVh0U29mdHdhcmUAd3d3Lmlua3NjYXBlLm9yZ5vuPBoAAABbSURBVCiRrdGxEUBgEEThn0SsAi1oQhm60IUulKEJLahALPIJzMXOjDez2QvudgtW9ChvKR4uLOgycnBiRpuRgwMTmowc7BhRZeRgw1CXL/xxRurBVHXpUdJz3ywRoVpFeU0qAAAAAElFTkSuQmCC) no-repeat !important;
}

/* Links */

a {
color:#c8c8c8 !important;
text-decoration:none !important;
border:0 !important;
}
a:hover {
color:#fff !important;
text-decoration:none !important;
}

/* Tables */

thead#tableHead th, table#searchResult th, table#searchResult td, table#searchResult th, table#searchResult td {
background:#3c3c3c !important;
border:0 !important;
}

tr .alt {
background:#2d2d2d !important;
}

thead#tableHead a {
color:#fff !important;
border:0 !important;
}

/* Forms */

input {
background:#2d2d2d !important;
color:#c8c8c8 !important;
border:1px solid #646464 !important;
}
input[type=submit] {
display:none !important;
}

/* Gets rid of those ****ing ugly ads */
#adbrite-banner, .ads, .ad, iframe {
display:none !important;
}
/* Update Code */
.uldetails label {
color:#fff !important;
}
img[src="http://static.thepiratebay.org/img/agb.gif"] {
display:none !important;
}
.morealbums li a:hover, .upcomingevents li a:hover {
background:#1e1e1e !important;
}
#topleft {
display:none !important;
}
input {
color:#101010 !important;
}
#comments dt {
background:transparent !important;
color:#686868 !important;
}
#irc {
background:transparent !important;
border:0 !important;
}
}
