css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp(".*forum\\.mods\\.de\/bb.*") {
	/*
		Bei den Ersatzbildern habe ich mir nicht wirklich
		Mühe gegeben, wer Lust hat, kann da ja mal was basteln,
		für mich wars dumm genug, einen CSS3-Hack dafür zu finden.
		bildbreite -> padding-left
		bildhöhe   -> height
	*/

	/* Bilder allgemein ausblenden, Kommentar entfernen wenn erwünscht */
	/*
	img[src]{opacity:0.0 !important;}
	img[src]:hover{opacity:100.0 !important;}
	*/

	/* Miniavatare :D */
	img[src*="avatare"] {
		max-height:40px !important;
		opacity:100.0 !important;
	}

	img[src*="/bb/img/smilies/"],
	img[src*="p0t.kicks-ass.net/"],
	img[src*="/bb/img/icons/"] {
		max-height:12px !important;
		opacity:100.0 !important;
	}

	/* Banner invertiert (Alle <img>, deren src mit "banner-mde.jpeg" endet) */
	img[src*="banner-"] {
		display: none !important; 
	}
	
	/* Button NewThread invertiert */
	img[src*="newthread"] {
		margin-top: 5em !important;
		width: 0 !important;
		height: 36px !important;
		padding-left: 97px !important;
		background-image: url("http://i.imgur.com/moTHT.gif") !important;
	}
	
	/* Button LoginToPost invertiert */
	img[src*="nonewthread"] {
		margin-top: 5em !important;
		width: 0 !important;
		height: 36px !important;
		padding-left: 99px !important;
		background-image: url("http://i.imgur.com/Lqt70.gif") !important;
	}
	
	/* Button NewReply invertiert */
	img[src*="newreply"] {
		margin-top: 5em !important;
		width: 0 !important;
		height: 36px !important;
		padding-left: 99px !important;
		background-image: url("http://i.imgur.com/ImRas.gif") !important;
	}
	
	/* Werbung für 3dsupply ausblenden (sorry, aber die Bilder sind viel zu auffällig) */
	a[href*="3dsupply.de"] {
		display: none !important; 
	}
	
	/* Kategorien farblich invertieren */
	body,div[id^="cat_div"] {
		background-color: #fff !important;
		color: #000 !important; 
	}
	
	/*
		Im Folgenden sind die farblichen Modifikationen verzeichnet.
		Für das billige Layout habe ich eine möglichst langweilige Palette
		generiert und die Farben ohne Ordnung verteilt.
		Welche Farbe wo landet war mir dabei herzlichst egal.
	*/

	/* Quickreply Script */
	#qr_row1,
	#qr_row2{
		background-color: #ccddbb !important;
	}

	/* New Button Script */
	.newInsertButton,
	input[type="submit"]{
		background-color: #bbccaa !important;
		color: #000000 !important;
	}

	.spoiler{
		background-color: #bbccaa !important;
		color: #bbccaa !important;
	}

	span.spoiler{
		color: #000000 !important;
	}

	span.marked{
		background-color: #CCDDBB !important;
	}
	
	/*
		In allen <td>,<tr> und <div> Farbe (.*)#07141C(.*) ändern
		(bei Bedarf auch * als Tag-Selektor)
	*/

	tr.b td,
	td.b,
	div[bgcolor*="#112233"],
	div[style*="#07141C"],
	td[bgcolor*="#07141C"],
	tr[bgcolor*="#07141C"],
	.note {
		background-color: #99aa99 !important; color:#fff; 
	}
	.color2 {
		background-color: #99aa99 !important; 
	}
	.color3x {
		background-color: #99aa99 !important; 
	}

	/* PM-System */
	tr.hh td, td.hh, tr.h td.hh{
		background-color: #bbccaa !important;
		color: #000000 !important; 
	}
	
	/* in allen td,tr,div Farbe 2 ändern */
	td[bgcolor*="#091827"],
	tr[bgcolor*="#091827"],
	div[style*="#091827"] {
		background-color: #bbccaa !important; 
	}
	select.quickmod {
		background-color: #bbccaa !important; 
	}
	
	/* in allen td,tr,div Farbe 3 ändern */
	tr[bgcolor*="#394E63"],
	td[bgcolor*="#394E63"],
	div[style*="#394E63"] {
		background-color: #ccddbb !important; 
	}
	
	/* in allen td,tr,div Farbe 4 ändern */
	tr[bgcolor*="#BCCACF"],
	td[bgcolor*="#BCCACF"],
	div[style*="#BCCACF"] {
		background-color: #779966 !important; 
	}
	
	/* in allen td,tr,div Farbe 5 ändern */
	td.h,tr.h td,
	tr[bgcolor*="#222E3A"],
	td[bgcolor*="#222E3A"],
	div[style*="#222E3A"] {
		background-color: #ccddcc !important; 
	}
	.color3b {
		background-color: #ccddcc !important; 
	}

	font[color*="#FFFFFF"] {
		color: #000000 !important;
	}
	
	/* in allen td,tr,div schwarze Flächen invertieren */
	tr[bgcolor*="#000000"],
	td[bgcolor*="#000000"],
	div[style*="#000000"] {
		background-color: #FFFFFF !important; 
	}
	
	/* in allen td,tr,div Farbe 6 ändern */
	tr[bgcolor*="#091827"],
	td[bgcolor*="#091827"]
	,div[style*="#091827"] {
		background-color: #bbccaa !important; 
	}
	.color1 {
		background-color: #bbccaa !important; 
	}
	
	/* in allen td,tr,div Farbe 7 ändern */
	tr[bgcolor*="#21354C"],
	td[bgcolor*="#21354C"],
	div[style*="#21354C"] {
		background-color: #eeffee !important;
		color: #000000 !important; 
	}

	/* Farbe 8 ändern */
	.color3 {
		background-color: #ccddbb !important; 
	}
	
	/* Farbe 9 ändern */
	.color3h {
		background-color: #99cc88 !important; 
	}
	
	/*
		Restliche Graustufen und Fliegendreck
	*/
	
	.bookmark,
	a.bookmark:visited {
		font-weight: bold;
		color: #A40 !important 
	}

	.ac_entry:hover, .ac_entry_active:hover {
		background-color: #A94 !important; 
	}
	
	.ac_entry_active {
		background-color: #BA5 !important; 
	}
	
	.overlay {
		border: 1px solid #420 !important;
		background-color: #864 !important;
		color: #eee !important; 
	}
	
	.editnote {
		color: #779966 !important; 
	}
	
	.editnote a:link, .editnote a:visited {
		color: #99cc88 !important;
	}
	
	.editnote a:active, .editnote a:hover {
		color: #ccddcc !important; 
	}
	
	.marked, .markedtext {
		color: #007 !important; 
	}
	
	.marked2 {
		color: #704 !important; 
	}
	
	.marked3 {
		color: #540 !important;
		background-color: #779966 !important; 
	}
	
	.markedposttext .posttext {
		color: #100 !important; 
	}
	
	.texthidden {
		color: #fff !important; 
	}
	
	a.bookmark:hover {
		color: #222 !important 
	}
	
	.bookmarklist,
	.bookmarklist hr {
		border: 1px solid #A40 !important;
		color: #666 !important; 
	}
	
	.bookmarklist span {
		color: #000 !important; 
	}
	
	.bookmarklist .bookmark_del_warn {
		color: #066 !important; 
	}
	
	.bookmarklist .bookmark_del {
		color: #0AA !important; 
	}
	
	.newpoll,
	a.newpoll:visited {
		color: #A40 !important 
	}
	
	a.newpoll:hover {
		color: #666 !important 
	}
	
	a:link {
		color: #333 !important 
	}
	
	a:visited {
		color: #555 !important 
	}
	
	a:hover {
		color: #222 !important 
	}
	
	a:active {
		color: #000 !important;
	}

	.marked, a.marked {
		background-color: #fff !important;
	}
	
	a.wht:link,
	a.wht:visited {
		color: #000 !important 
	}
	
	a.catlink:link {
		color: #fff !important; 
	}
	
	a.nu:visited,
	a.catlink:visited {
		color: #475 !important; 
	}
	
	a.catlink:active {
		color: #fff !important; 
	}
	
	.option {
		background-color: #fff !important;
		border: 1px solid #fff !important; 
	}
	
	.copyright {
		color: #475 !important; 
	}
	
	.legende {
		color: #000000 !important; 
	}
	
	.on {
		background-color: #cfc !important; 
	}
	
	.off {
		background-color: #fcc !important; 
	}
	
	input.bordered,
	textarea {
		border: #fff solid 1px !important; 
	}
	
	.ac  {
		background-color: #fed !important;
		border: 1px solid #fff !important; 
	}
}
