css:
@charset "utf-8";
@-moz-document url-prefix(http://www.google.co.jp/ig),url-prefix(http://www.google.com/ig) {
	body{
		background:#FFFF ! important;
	}
	body,td,th{
		line-height:1.5 ! important;
		color:#333 !important;
	}
	a {
		color: #2444A6 ! important;
		text-decoration: none ! important;
	}
	a:link {
		color: #2444A6 ! important;
		text-decoration: none ! important;
	}
	a:visited {
		color: #2444A6 ! important;
		text-decoration: none ! important;
	}
	a:hover {
		color: #2444A6 ! important;
		text-decoration: underline ! important;
	}
	div#sf {
		display:none ! important;
	}
	h2.modtitle{
		border-bottom:#4495D0 4px solid ! important;
		background:#7fcbdf ! important;
	}
	table.mhdr{
		border-top:none ! important;
	}
	td.mttl{
		background:transparent ! important;
		padding:2px 5px 2px 2px ! important;
	}
	td.mttl a{
		color:#FFF ! important;
		text-decoration:none ! important;
	}
	td.medit{
		background:transparent ! important;
		padding:2px 5px 2px 2px ! important;
	}
	div.modbox{
		border-top:1px solid #DDDDEE;
		border-left:1px solid #DDDDEE;
		border-right:1px solid #888899;
		border-bottom:1px solid #888899;
		-moz-border-radius-bottomright: 6px;
		line-height:1.8 ! important;
	}
	div.modbox div.mc{
		padding:0 ! important;
		border-bottom:1px dotted #CCC;
	}
	div.modbox div.mc a:only-child{
		display:block;
		padding:4px;
		margin-right:4px;
		text-overflow-mode:ellipsis;
		white-space:nowrap ! important;
		overflow:hidden;
	}
	div.modbox div.mc a:only-child:before{
		content:"ï½¥ ";
	}
	div.modbox div.mc a:only-child:visited{
		background-color:#F6F6F6;
		color:#B4C2EF ! important;
	}
	div.modbox div.mc a:only-child::visited:before{
		content:"ï½¥ ";
	}
	div.modbox div.mc a:only-child:hover{
		background-color:#F0FDE2;
	}
	div#GM31label{
		background-color:#F2F2F2 ! important;
		padding:0 4px ! important;
		border-bottom:3px double #EEE ! important;
	}
}
