css:
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {





#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue, #dashboard_followers, #dashboard_members{







background-image:url('http://i54.tinypic.com/x54mbk.png') !important;







background-repeat:no-repeat !important;







background-attachment: fixed;







background-color:#C9C9C9 !important;







background-position:right !important







}







#dashboard_index div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content, #dashboard_post_queue div.l-content, #dashboard_members #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {







background: rgba(218,190,150,0.7) !important;







}





#dashboard_edit_post{







background-color: #C9C9C9 !important





}





#right_column .dashboard_nav_item {







border-bottom:0px solid #eee !important;







background-image:none  !important







}







#right_column .dashboard_nav_item .dashboard_nav_title,







                    #right_column .dashboard_nav_item .dashboard_nav_title a,







                    #right_column .dashboard_nav_item a,







                    #right_column .dashboard_nav_item ul.dashboard_subpages,







                    #right_column .dashboard_nav_item ul.dashboard_subpages li a {







                        color:#000000 !important;





}







#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {







                        color:#888 !important ;







                    }







#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {







                        background: none !important;







                    }







#left_column #posts .notification .avatar {







                        border-color:transparent !important;







                        background-color:#FF70BA !important;







                    }







#left_column #posts .notification .avatar:hover {opacity:1 !important;}







.post{opacity:1 !important; border-bottom:0px !important; color:#555555}







.post:hover{opacity:1 !important}







.permalink{background-color: #000000 !important; background-image:none !important}







.active{background-color:#dabe96 !important; color:#999999 !important;}







.nav_item_nipple{display:none !important}







.nav_menu{background-color:#E791A5 !important; border-bottom:0px !important; color:#000000 !important;}







#nav .nav_item.active,







#nav .nav_item .nav_menu,







#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {







background-color:#dabe96 !important;







background-image:none !important;







border:0px !important;







color:#000000 !important;







}







#nav .nav_item .nav_menu {







border-bottom:0px !important







}





#nav .nav_item .nav_menu a {







color:#DF7890 !important;







border-top-color:#322115 !important;







}







#nav .nav_item .nav_menu a:hover {







color:#8B8B8B !important;}







.dashboard_switch_blog_menu_header{







background-color:#17130d !important;







color:#777 !important;







}







.dashboard_switch_blog_menu_item{







background-color:#ffffff !important;







color:#777 !important;







border:0px !important







}







.dashboard_switch_blog_menu_footer{







background-color:#ffffff !important;







color:#777 !important;







}







.dashboard_switch_blog_menu_wrapper{







background-color:#ffffff !important;







}







#new_post_notice_container{







color:#fff !important







}







#new_post_notice_container a{







color:#fff !important







}







#dashboard_switch_blog_menu_current,







#dashboard_switch_blog_arrow_and_divider,







#dashboard_switch_blog_arrow_current







{background-image:none !important}







#footer{







background: #f8e4c3 !important;







background-image:none !important







}







/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
.new_post {
	background: #ffffff url('http://assets.tumblr.com/images/new_post_icons_no_labels.png') center center no-repeat !important;
}
.new_post_label_icon {
	background-image: none !important;
}













.new_post_label{



font-size:14px !important;



padding-top:5px !important;



font-family:Georgia, Times New Roman, sans-serif !important;



font-style:italic !important;



text-transform: none !important;



color:#Ffffff !important;



letter-spacing:1px !important



}







#left_column #posts .notification {



                        background-color:#ffffff !important;



                        color:#DD76C0 !important;



                        border-bottom-color: #efdfc4 !important



                    }







#left_column #posts .notification .block {



                            color:#DD76C0 !important;



                        }







#left_column #posts .notification.alt {



                        background-color: #ffffff !important;



border-bottom-color: #efdfc4 !important



                    }







#left_column #posts .notification.last_notification,





                    #left_column #posts .notification.single_notification {



                        border-bottom-color:#ffffff !important;



                    }







#left_column #posts .notification a {



                        color:#68574a !important;



                        border-bottom:0px !important;



                    }







#left_column #posts .notification blockquote {



                        border-left-color:#eee !important;



                    }







#left_column #posts .notification .avatar {



                        border-color:transparent !important;



                        background-color:#ffffff !important;



                    }







#left_column #posts .notification .avatar:hover{



                    }







.notification_type_icon{display:none !important}







h1,h2,h3,h4,h5,h6{color:#000000 !important}







}
