css:
@namespace url(http://www.w3.org/1999/xhtml);      @-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/') 


 {#dashboard_index{background: url('http://i53.tinypic.com/2vcjq8i.jpg') center right #000000 repeat fixed !important; letter-spacing: -1px;}{background-color:#000000 !important; opacity:.8;}
body {background: url('http://i54.tinypic.com/2m5dq3o.jpg') center right #000000 repeat fixed !important;}  *{text-shadow:none !important; letter-spacing: -1px;}
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:#000000 !important; opacity: 8;}
ol#posts li.notification{background-color: #000000 !important; border-bottom: 1px solid #A59AA0 !important; color: #000000 !important;}
#right_column a#dashboard_switch_blog_menu_current:hover {background:#3e3e40;}
#nav .nav_item.active{background-color:#000000;}
#left_column #posts .post .permalink{background-color:#000000;}
#nav .nav_item.active,
#nav .nav_item .nav_menu,
#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
background-color:#000000 !important;
background-image:none !important;
border:0px !important}
#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current
{background-image:none !important}
.notification_type_icon{display:none !important}
#left_column #posts .notification {
background-color:#000000 !important;
color:#666 !important;
border-bottom-color: #eee !important}
#new_post_notice_container{
color:#fff !important}
#new_post_notice_container a{
color:#fff !important}
ol#posts li.post.new_post {
background: #ffffff url('http://i51.tinypic.com/5v1641.jpg') center center no-repeat;}
img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
    width:0;
    height:70px;
    padding-right:250px;
    opacity: 7;
}
ol#posts .post.new_post .new_post_label {
display:none !important;

}


}
