css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
url-prefix('http://forums.riftgame.com')
{

body, legend {
  color:#333!important;
}

a {color: #069!important;}

a:hover {color:#0000ff!important}

.blockbody  {
  background: #ececec none !important  ;
  color:#333!important;
  }

.blockrow {
  background: #f0f0f0 none !important  ;
  color:#333!important;
  }
.blockhead, .blockfoot{
  background-color:#ceced4!important;
  color:#333!important;
  background-image:none!important
 }

.blocksubhead {
 background-color:#ddd!important; 
 color:#333!important; 
 background-image:none!important; 
 border-top: none!important; 
}
.prof_blocksubhead {
  background-color:#ececec!important; 
  }
.wgo_block .section {
  color:#333!important
  }
.blockhead, .blocksubhead {font-weight: bold!important}

dl.stats dt, dl.stats dd {color:#333!important}

.body_wrapper  {background-color:#ddd!important;}

.editor_textbox textarea, .forum {background-color:#fff!important}

.postbody {background-color:#f0f0f0!important; }
.postbody .content {color:#333!important;}

.breadcrumb .navbit a, .breadcrumb .lastnavbit span {border:none!important}

#toplinks a, .forumhead a, #navbar a, .navlinks a, #footer a,
.above_threadlist_controls a, .threadlisthead a, .searchlisthead a, .thread_controls a, 
.posthead a, .postfoot a, .tabslight a,
#searchtypeswitcher a {color:#fff!important}

#announcements a:hover {color:#CEDFEB!important}
#announcements a {color:#66ccff!important}
#announcements {color:#fff!important}
.popupbody li a { color:#fff!important;}
#newreplylink_bottom, #newreplylink_top, #newthreadlink_bottom, #newthreadlink_top  { color:#fff!important;}

.forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow {
  background: #ececec none !important;
  color:#333!important;
  }
.forumdata, .forumrow .forumlastpost, .forumstats, .forumstats_2 {
  color:#333!important;
  }

.shade {
	color:#333!important;
}
.time {
  color:#999!important;
}

.threadbit {color:#333!important}

.threadbit .sticky, .threadbit .sticky .threadlastpost, .threadbit .sticky .threadpostedin {
  background: #e1e1f2 !important;
  }

.threadbit .nonsticky, .threadbit .discussionrow, .threadbit .nonsticky .threadlastpost, .threadbit .nonsticky .threadpostedin {
  background: #ececec !important;
  }

.threadstats, .threadbit .pagination a {background:none!important}

.postdetails, .postbody .title {color:#333!important}

.postpagestats, .threadpagestats {color:#333!important}

.pagination a{color:#333!important; background: none !important}

#sidebar_container{ background-color:#ececec!important;}

#showthread_navpopup a.popupctrl, 
#showthread_navpopup a.textcontrol,
.groupcontrols a.textcontrol,
#pollinfo a.textcontrol,
#new_subscribed_threads a.textcontrol{
  background:url("http://forums.riftgame.com/images/gradients/generic_button.png") repeat-x scroll left top rgb(204, 204, 255)!important; 
}

.likedate, .description {
  color:#585858!important;
}

.forumfoot .smallfont {color:#888!important}

#pollinfo {background-color:#ddd!important; }
#pollinfo .blockfoot {margin-left:0!important}
.polloptionsblock {
  background: #ceced4 url(http://forums.riftgame.com/images/misc/poll_question_mark.png) no-repeat 30px 10px!important;}
#pollinfo .blockhead, #pollinfo .blockfoot  {padding-left:5px!important; padding-right:5px!important; background-color:#ddd!important}
#pollinfo .polltitle {color:#333!important}

#vbseo-likes { background-color:#ddd!important; }
.vbseo-likes-count{color:#069!important}

.popupbody li > a:hover,
.popupbody li > label:hover,
.popupbody li.vbmenu_hilite > a {
	background-color:rgb(102, 204, 255)!important;
	color:rgb(255, 255, 255)!important;
}
.navpopupbody li.optionlabel {color:#fff!important}

.bbcode_container div.bbcode_code,
.bbcode_container pre.bbcode_code
{background-color: #f0f0f0!important}


#likelist li {
  background-color:#ececec!important;
  border: 1px!important;
  border-color: #333!important
  }
#likelist {background-color:#ceced4!important; }
#likelist .subsectionhead {color:#333!important; padding-left:6px!important}

.postbit .userinfo, .postbit .userinfo_noavatar {
  background-color:#ddd!important
  }
}
