/*-----------------------------------
| Google - The Darkness by KrystalEmpire
| http://krystalempire.deviantart.com
| V0.4
-----------------------------------*/
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("google.com"), url-prefix(http://www.google.de), url-prefix(http://www.google.it), url-prefix(http://www.google.fr), url-prefix(http://www.google.co.uk) {

/* Main */

body {
background:#3c3c3c !important;
color:#c8c8c8 !important;
}

/* Links */

a {
color:#c8c8c8 !important;
}

a:hover {
color:#fff !important;
}

/* Logo Switcharoo */

img[src*="/intl/en_ALL/images/logo.gif"], img[src*="/intl/en_ALL/images/images_hp.gif"], img[src*="/intl/de_de/images/logo.gif"], img[src*="intl/it_it/images/logo.gif"], img[src*="/intl/fr_fr/images/logo.gif"], img[src*="intl/en_uk/images/logo.gif"], img[src*="/logos/"] {
margin-top: 5em !important;
width: 0px !important;
height: 200px !important;
padding-left: 500px !important;
background:url(data:image/png;base64,%3D%3D) no-repeat !important;
}

/* Forms */

input[name=q] {
width:200px !important;
height:40px !important;
font-size:24px !important;
color:#c8c8c8 !important;
background:#2d2d2d !important;
border:1px solid #646464 !important;
text-align:center;
}

input[name=btnI] {
display:none !important;
}

input[type=submit] {
display:none !important;
}

/* Get rid of the crap */

a[href*="/advanced_image_search"], a[href*="/preferences"], a[href*="/language_tools"], a[href*="/advanced_search"], a[href*="/intl/en/ads/"], a[href*="/services"], a[href*="/intl/en/about.html"], a[href*="http://www.google.ca/"] {
display:none !important;
}

center p font,center font, #guser, #gbar, .gbh {
display:none !important;
}

#sd, .t {
display:none !important;
}

#logo {
display:none !important;
}

a[href*="http://news.google.com/"] {
display:none !important;
}

/* Misc Styles */

.t  {
background:none !important;
border:0 !important;
color:#c8c8c8 !important;
}

#sd {
color:#c8c8c8 !important;
}

font {
color:#c8c8c8 !important;
}

.j, .j font {
color:#c8c8c8 !important;
}

.a {
color:#fff !important;
}

.l b {
color:#fff !important;
}

.csb, .n div, #logo span {
background:none !important;
}

.n a, .n .i {
text-decoration:none !important;
}

.i {
color:#fff !important;
}

.b a {
font-size:10pt !important;
}

}
