css:
@-moz-document url-prefix("http://www.forocoches.com/") {
/* Estilos generales de la web */

/* ********************************************* */
/* *********** VISIBILIDAD BLOQUES ************* */
/* ********************************************* */
/* Banner superior */
body > div:nth-of-type(1),
body > div:nth-of-type(2) { display: none!important; }

/* ***************** Fuentes******************* */

body, .navbar, .smallfont, td, th, p, li, b {
    /*font: 11px verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif;*/
    font-family: 'Segoe UI','Lucida Grande',Verdana,Arial,Helvetica,sans-serif !important;
}

/*body {
    font-size: 0.8em !important;
}*/
/* 
90% "Trebuchet MS",verdana,arial,sans-serif 
0.8em/145% georgia,times,serif;
*/


/* ***************** Tamaños ******************* */

navbar_notice, .navbar_notice font, .navbar_notice b {
    font-size: 11px !important; /* tbn #E8E8E8 */
}

.cmega {
    font-size: 14px !important;
}

/* ***************** Estilos ******************* */

/* Quito la negrita por defecto al listado de hilos */
#threadslist a, 
.cajasnews a,
.tborder strong, 
.tborder .smallfont b
{ 
    font-weight: normal !important;
}

/* ***************** Colores ******************* */

body, page, .alt1, .alt1Active, .alt2, .alt2Active { 
    background-color: #FFFFFF !important;
    color: #666666 !important;
}

.alt2, .alt2Active {
    background-color: #E8E8E8 !important;
}

#tablist a { background-color: rgb(245, 245, 245) !important;}
#tablist a.current { background-color: #fff !important;}



/* ***************** Enlaces ******************* */

a:link, body_alink {
    color: #0066DD !important;
    text-decoration: none !important;
}

a:visited {
    color: #70579D !important; /* #70579D */
}
/* #70579D #563867 #394E66 #4F6B8E #4F6B8E #1B3350 #00306A*/

a:hover, a:active, body_ahover {
    color: #FF6600 !important;
}

/* Cabecera Temas Foro y Subforos */
.tcat a:link { color: #666666 !important; }
.tcat a:hover { color: #FF6600 !important; }

/* NavBar */
.navbar a:link, .navbar_notice a:link { color: #666666 !important; }
.navbar a:hover, .navbar_notice a:hover { color: #FF6600 !important; }

/* Listado de hilos */
/*#threadslist a:link { 
    color: #666666 !important;
}*/

#threadslist a:hover { 
    color: #FF6600 !important;
}

/* Paginador */
.pagenav a:link, .pagenav a:visited {
color: #666666 !important;
}

.pagenav a:hover {
color: #FF6600 !important;
}

/* Usuario */
    a.bigusername:link { color: #333333 !important; }
    a.bigusername:hover { color: #FF6600 !important; }



/* ***************** Bloques ******************* */
/* Limpieza de bordes */
.tborder {
    background: #D1D1E1 !important; /* Color del borde */
    border: none !important;
}

/* Barra de registro, Herramientas de navegacion de pagina e hilos */
.vbmenu_control { background: #C6C6C6 !important; }

/* Titulo Foro */
#table3 { background: none !important; }
#table3 td { padding: 0 !important; margin: 0 !important; }
.cmega2 { font-size: 12pt !important;}

#AutoNumber1 td { background: none;}



/* Barra de avisos y noticias */
.navbar_notice table { background: none !important; }

.navbar_notice td { background: none !important; padding: 0 !important; }

.navbar_notice { background: none!important;}

/* Cabecera Temas Foro y Subforos */
.tcat { background: #E8E8E8 !important; color: #666666 !important;}
.vbmenu_control { color: #666666 !important; }

.vbmenu_control a:link { color: #666666 !important; }
.vbmenu_control a:hover { color: #FF6600 !important;  }
.vbmenu_option, .vbmenu_hilite { background: none !important; }
.vbmenu_option a:link, .vbmenu_option a:visited, .vbmenu_hilite a:link, .vbmenu_hilite a:visited { color: #666666 !important; }
.vbmenu_option a:hover, .vbmenu_hilite a:hover{ color: #FF6600 !important; }

/* Cabeceras de columnas lista de temas y separadores */
.thead { color: #666666 !important; background: #F6F6F6 !important; }
.thead a:link { color: #666666 !important; }
.thead a:hover{ color: #FF6600 !important; }

/* Anuncios internos */
.cmega { background: none !important; }
}

@-moz-document url-prefix("http://www.forocoches.com/foro/") {
/* Estilos específicos para el foro */

/* vBulletin 3 CSS For Style 'ForoCoches' (styleid: 5) */

/* ********************************************* */
/* *********** VISIBILIDAD BLOQUES ************* */
/* ********************************************* */

/* Menu lateral */
td[width="170"] { display: none!important; }

td[width="15"] { display: none!important; }

/* Basurilla y espaciados generales*/
body > table { display: none!important; }

/* Publicidad superior de foros */
table#table5 { display: none!important; }
table[style="margin-bottom:10px"] { display: none!important; }
td[bgcolor="#d5e5ee"] { display: none!important; }

/* Publicidad inferior */
table.cajasprin { display: none !important; }

/* Muestro barra navegacion */
body > table[class="cajasprin"]:nth-of-type(3) { display: block !important;} 

/* ********************************************* */
/* ***************** PARCHES ******************* */
/* ********************************************* */    
.alt2, .alt2Active {
    max-height: none !important;;
}    
}
