css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://blog.livedoor.jp/imhere_kazhayashi/") {
body{
	background-color:#EFEFEF!important;
	background-image:none !important;
}
#banner{
	margin:-40px 0px 0px 0px !important;
	width:750px !important;
}
/*バナークリックでtopに移動*/
div#container div.blog div.blogbody div.posted div.menu a.amenu[href="http://blog.livedoor.jp/imhere_kazhayashi/"]:after{
	content:"";
	font-size:10px!important;
	border-left:750px solid transparent !important;
	border-top:225px solid transparent !important;

	position:absolute!important;
	top:0px!important;
	left:0px!important;
}
/*topページ*/
div#container div#content{
	padding-top:0px !important;
}
div#container div#content div.blog{
	text-align:left !important;
}
/*ポストタイム*/
div#container div#content div.blog div.blogbody div.posted{
	margin-bottom:20px !important;
}

div.sidetitlebody div.sidetitle{
	font-size:14px !important;
	color:#5F5F5F!important;
}
div#links div.plugin-recent_articles div.side
,div#links div.plugin-recent_comments div.side{
	font-size:13px !important;
	color:#5F5F5F!important;
}
div#links div.plugin-monthly div.side div form
,div#links div.plugin-qr_code div.side div
,div#links div.syndicate{
	font-size:13px !important;
	text-align:left !important;
}
div#links table.checkers{
	font-size:13px !important;
	width:220px !important;
	text-align:left !important;
}
.sidewrapper{
	margin-bottom:10px !important;
}
div#container div#content div.blog div.pagetop{
	text-align:right!important;
	margin-top:10px !important;
}

#container{
	position:relative !important;
	background-image:none !important;
	width:750px !important;
	text-align:left !important;
	overflow:hidden !important;

	margin-bottom:5px !important;
	border-bottom:2px solid #DFDFDF!important;
}
div#container div.blog{
	font-family:
	"ヒラギノ角ゴ Pro W3", "Hiragino Kaku Gothic Pro",
	"メイリオ", "Meiryo",
	"Osaka",
	"ＭＳ Ｐゴシック","ＭＳ ＰGothic",
	sans-serif !important;
	font-size:14px !important;
	color:#5F5F5F!important;

	margin-top:10px !important;
	padding-left:20px !important;
	padding-right:20px !important;
}
div#container div.blog div.blogbody div.posted div.menu{
	text-align:center!important;
	margin-top:10px !important;
}
div#container div#content{
	margin-top:0px !important;
}
div#container div#links{
	margin-top:40px !important;
}
div#container div.blog div.blogbody h3.title{
	background-image:none !important;
	border:none !important;
	margin:0px 0px 30px 0px !important;
	padding:0px 0px 0px 0px !important;
}
/*コメント*/
div#container div.blog div.blogbody div.comments-head{
	margin-top:30px !important;
	margin-bottom:15px !important;
}
div#container div.blog div.blogbody div.comments-body
,div#container div.blog div.blogbody div.comments-post{
	font-size:13px !important;
}
div#container div.blog div.blogbody div.comments-post{
	font-size:13px !important;
	color:#478F78!important;
	margin-bottom:20px !important;
}
/*フォーム*/
div#container div.blog div.blogbody table.form{
	font-family:
	"ヒラギノ角ゴ Pro W3", "Hiragino Kaku Gothic Pro",
	"メイリオ", "Meiryo",
	"Osaka",
	"ＭＳ Ｐゴシック","ＭＳ ＰGothic",
	sans-serif !important;
	font-size:14px !important;
	color:#5F5F5F!important;

	margin-top:30px !important;
}
div#container div.blog div.blogbody table.form tbody tr td:nth-of-type(1){
	width:75px !important;
}
/*ページトップ*/
div#container div.blog div.blogbody div.pagetop{
	text-align:center !important;
	margin-top:10px !important;
	margin-bottom:10px !important;
}
/*リンク色*/
a:link
{
	color:#6192DF!important;
	text-decoration:none!important;
}
a:visited{
	color:#9F9F9F!important;
}
a:hover{
	color:#FF8D4F!important;
	text-decoration:none!important;
}
/*広告削除*/
div#container div.blog div#ad_rs.ad_rs_b
,div#container div.blog div.blogbody div#ad2
,div#header2{
	display: none!important;
}

@media screen and (-webkit-min-device-pixel-ratio:0){
div#container div.blog div.blogbody{
	background-color:#FFFFFF !important;
	background-image:none !important;
}
div#container div.blog h2.date{
	font-size:18px !important;
	color:#5F5F5F!important;
	margin:0px 0px 0px 30px !important;
	padding:0px 0px 0px 0px !important;
}
div#container div.blog div.blogbody h3.title{
	font-size:16px !important;
	background-image:none !important;
	border:none !important;
	margin:0px 0px 0px 20px !important;
	padding:0px 0px 0px 0px !important;
}
div#container div.blog div.blogbody div.main{
	font-size:14px !important;
	color:#5F5F5F!important;
}
div#container div.blog div.blogbody div.posted
,div#container div.blog div.blogbody div.posted div.menu{
	font-size:12px !important;
}

div#container div.blog div.blogbody div.comments-head{
	font-size:12px !important;
	background-image:none !important;
	border:none !important;
	margin:0px 0px 0px 20px !important;
	padding:0px 0px 0px 0px !important;
}
div#container div.blog div.blogbody div.comments-body{
	font-size:12px !important;
	color:#5F5F5F!important;
}
div#container div.blog div.blogbody table.form tbody tr td textarea#text
,div#container div.blog div.blogbody table.form tbody tr td input#author
,div#container div.blog div.blogbody table.form tbody tr td input#email
,div#container div.blog div.blogbody table.form tbody tr td input#url{
	background-color:#FEFEFE!important;
}
div#container div#links div.sidewrapper div.sidetitlebody div.sidetitle
,div#container div#links div.sidewrapper div.calbody table tr td{
	background-image:none !important;
}
html body div#container div#links div.plugin-qr_code div.side div{
	text-align:center !important;
}
div#container div#content div.blog div.blogbody
,div.blogbody{
	border:none !important;
}
div#container div#links div#calendarplugin-331 div.calendarheadbody div#calendarhead-331{
	color:#5F5F5F!important;
	background-image:none !important;
}
}
}
