css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {





*{text-shadow:none !important; color:#565656 !important;}



Body{

background-color:#f5f5f5 !important;

color:#565656 !important;

font-size:12px !important;

background: url('http://i53.tinypic.com/1zzhkz4.png') center right #000000 repeat fixed !important;

}



#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

background-color:transparent !important

}



#left_column #posts .post .permalink {

background-color:#f5f5f5 !important;

}



#left_column #posts .post .post_avatar,

#left_column #posts .post .sub_avatar .sub_avatar_inner {

background-color: #fbfbfb !important;

border-bottom-color: #eee !important;

}



#left_column #posts .post{

border-bottom:1px #eee solid !important

}



#nav .nav_item.active,

#nav .nav_item .nav_menu,

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {

background-color:#fbfbfb !important;

background-image:none !important;

border:0px !important

}



#nav .nav_item .nav_menu {

border-bottom:0px !important

}



#nav .nav_item .nav_menu a {

color:#888 !important;

border-top-color:#eee !important;

}



#nav .nav_item .nav_menu a:hover {

color:#666 !important;

}



#footer, #footer a {

color:#666 !important;

background-color:#fbfbfb !important;

background-image:none !important;

}



.no_posts_found, h1.dashboard_header, h1.dashboard_header a, h1.dashboard_header span {

color:#ccc !important

}



#right_column .dashboard_nav_item {

border-bottom:1px solid #eee !important;

background-image:none  !important

}



#right_column .dashboard_nav_item .dashboard_nav_title,

                    #right_column .dashboard_nav_item .dashboard_nav_title a,

                    #right_column .dashboard_nav_item a,

                    #right_column .dashboard_nav_item ul.dashboard_subpages,

                    #right_column .dashboard_nav_item ul.dashboard_subpages li a {

                        color:#666 !important;

}



#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {

                        color:#888 !important ;

                    }



#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {

                        background: none !important;

                    }



#show_radar a, #hide_radar a,

                        #hide_recommended_blogs a, #show_recommended_blogs a {

                            color:#666 !important;

                        }





#left_column #posts .notification {

                        background-color:#fbfbfb !important;

                        color:#666 !important;

                        border-bottom-color: #eee !important

                    }



#left_column #posts .notification .block {

                            color:#333 !important;

                        }



#left_column #posts .notification.alt {

                        background-color: #f7f7f7 !important;

                    }

#left_column #posts .notification.last_notification,

                    #left_column #posts .notification.single_notification {

                        border-bottom-color:#ececec !important;

                    }

#left_column #posts .notification a {

                        color:#565656 !important;

                        border-bottom:0px !important;

                    }

#left_column #posts .notification blockquote {

                        border-left-color:#eee !important;

                    }



#left_column #posts .notification .avatar {

                        border-color:transparent !important;

                        background-color:#fbfbfb !important;

opacity:0.5 !important;

                    }



#left_column #posts .notification .avatar:hover{

opacity:1 !important;

                    }



.dashboard_switch_blog_menu_header{

background-color:#fbfbfb !important;

color:#666 !important;

}

.dashboard_switch_blog_menu_item{

background-color:#fbfbfb !important;

color:#666 !important;

border:0px !important

}

.dashboard_switch_blog_menu_footer{

background-color:#fbfbfb !important;

color:#666 !important;

}

.dashboard_switch_blog_menu_wrapper{

background-color:#fbfbfb !important;

}



#new_post_notice_container{

color:#fff !important

}



#new_post_notice_container a{

color:#fff !important

}



#dashboard_switch_blog_menu_current,

#dashboard_switch_blog_arrow_and_divider,

#dashboard_switch_blog_arrow_current

{

background-image:none !important

}

.notification_type_icon{

display:none !important

}





h1,h2,h3,h4,h5,h6{color:#555555 !important}



a, a:visited{color:#666666 !important}



a:hover{color:#888888 !important}
ol#posts li blockquote,







body.mceContentBody blockquote {







margin-left: 0 !important;







margin-right: 0 !important;







padding-left: 10px !important;







border-width: 4px !important;







border-color: #000000 !important;







}







ol#posts li blockquote blockquote,







body.mceContentBody blockquote blockquote {







border-color: #000000 !important;







}







ol#posts li blockquote blockquote blockquote,







body.mceContentBody blockquote blockquote blockquote {







border-color: #000000 !important;







}







ol#posts li blockquote blockquote blockquote blockquote,







body.mceContentBody blockquote blockquote blockquote blockquote {







border-color: #000000 !important;







}







ol#posts li blockquote blockquote blockquote blockquote blockquote,







body.mceContentBody blockquote blockquote blockquote blockquote blockquote {







border-color: #000000 !important;







}







#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



    background color: #ffffff; !important;



  }







#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



    background:#A09993 !important;



    -webkit-border-radius: 20px;



    -moz-border-radius: 20px;



    border-radius: 20px;



    margin: auto;



    padding: 20px 20px;



  }







h1 {



color: #A09993;



text-shadow: 0 0 3px #000000;



}







.no_posts_found, h1.dashboard_header {



color: #000000;



}







img#content_top, img#content_bottom {



display: none;



}







#right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {



    background: none;



  }







#right_column .dashboard_nav_item {



    background: none repeat-x;



    background-attachment: initial;



    background-position-x: 50%;



    background-position-y: 100%;



    background-origin: initial;



    background-clip: initial;



    background-color: initial;



}







#right_column .dashboard_nav_item #hide_radar {



background-image: none;



background-repeat-x: no-repeat;



background-repeat-y: repeat;



background-attachment: initial;



background-position-x: 0%;



background-position-y: 0%;



background-origin: initial;



background-clip: initial;



background-color: #A09993;



}







#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {



background: rgba(0,0,0,0.0);



}







#nav .nav_item.active {



    background: rgba(00,0,0,0.5) !important;



  }







#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {



    background: rgba(00,0,0,0.5) !important;



  }







ol#posts li.notification.alt {



    background-color: rgba(0,0,0,0.5);



}







ol#posts li.notification.first_notification {



    background-color: rgba(0,0,0,0.5);



}







ol#posts li.notification {



    background-color: rgba(0,0,0,0.5);



    border-bottom: 1px solid #000000;



}







ol#posts li.notification.last_notification {



    border-bottom: 1px solid #000000;



}







ol#posts li.post .permalink {



    background: rgba(00,0,0,0.5);



}







#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {



background: rgba(0,0,0,0.8);



border: solid #000000;



}







ol#posts li.post .post_avatar{



border-bottom: 0px solid #000000;



}







#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {



border-top: 1px solid #000000;



border-bottom: 1px solid #000000;



}







#right_column a#dashboard_switch_blog_menu_current:hover {



background: none;



}







#right_column a#dashboard_switch_blog_menu_current:hover #dashboard_switch_blog_arrow_current {



background: none;



}







form.dashboard_options_form {



background-color: rgba(0,0,0,0.0);



border-bottom: 1px solid #000000;



border-bottom-left-radius: 10px 10px;



border-bottom-left-radius: 10px 10px;



border-bottom-right-radius: 10px 10px;



border-bottom-right-radius: 10px 10px;



border-top-left-radius: 10px 10px;



border-top-left-radius: 10px 10px;



border-top-right-radius: 10px 10px;



border-top-right-radius: 10px 10px;



color: #000000;



display: block;



font-size: 13px;



margin-bottom: 25px;



padding: 15px 22px;



}







form.dashboard_options_form .option_container {



border-top: 1px solid #000000;



}







.no_posts_found, h1.dashboard_header {



color: #A09993;}



ol#posts li.post.new_post {

background: #ffffff;

}



img[src*="http://i46.tinypic.com/257oe84.jpg"]{

    width:0;

    height:70px;

    padding-right:250px;

    opacity: 0;

}


.sub_avatar{width:32px !important; height:32px !important; background-color:transparent !important}





  }
