css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("myth-weavers.com")  {
  a.ooc {
    text-decoration: none !important;
    /* border-bottom:1px dotted #571A19; */
  }
  body, div#headerContainer + div[align=center] {    /* Page Bg */
    background: #9C8E71 !important; /*darker: #9C8E71, orig #BCAB90 */
  }
  #headerContainer, #headerShadow {   /* Header Bg */
    background: #52251C !important;
    background: -webkit-gradient(linear, left top, left bottom, from(#52251C), to(#7A3D30)) !important; /* Webkit */
    background: -moz-linear-gradient(top, #52251C, #7A3D30) !important; /* FF3.6+ */     
  }
  #headerShadow { width:990px; background: transparent !important; }
  .page, .postHeader {                           /* Middle Area Bg */
    background: #E2D2AE !important; }
  .page {
    -moz-border-radius-bottomleft:0.5em;  -webkit-border-bottom-left-radius:0.5em;
    -moz-border-radius-bottomright:0.5em; -webkit-border-bottom-right-radius:0.5em;    
  }
  .alt1 {
    -moz-border-radius:0.5em;
    -webkit-border-radius:0.5em;      
   }
  #headerInner {
    background: transparent !important;
  }
  .postHeader {
    background: -webkit-gradient(linear, left top, left bottom, from(#E2D2AE), to(#C6AE7B)) !important; /* Webkit */
    background: -moz-linear-gradient(top, #E2D2AE, #C6AE7B) !important; /* FF3.6+ */
    -moz-border-radius:0.5em;
    -webkit-border-radius:0.5em;
  }
  .postbit {border-top:none !important;}
  div.postControlsTop a {
    -moz-border-radius-topleft:0.5em;  -webkit-border-top-left-radius:0.5em;
    -moz-border-radius-topright:0.5em; -webkit-border-top-right-radius:0.5em;
  }
  .tcat, #navbar {                               /* Chapter & Nav Bars */
    background: #4C241A !important;
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0,#5C3C1F), color-stop(0.5,#4C241A), color-stop(1,#5C3C1F)) !important;
    background: -moz-linear-gradient(top, #5C3C1F, #4C241A, #5C3C1F) !important; /* FF3.6+ */
    -moz-border-radius:0.5em;
    -webkit-border-radius:0.5em;
  }
  .alt1, .selectlist, .postContent, .postMenu, .postControlsTop a, .buttonbar {
    background: #F5EDE0 !important;              /* Post Content */
  }
  ul.buttonbar li:hover, ul.buttonbar li.selected {
    background: #E5DDD0 !important; /* Selected Buttonbat Item */
  }
  ul#threadgroups li, ul#threadgroups li.selected {
    -moz-border-radius-topleft:0.5em;  -webkit-border-top-left-radius:0.5em;
    -moz-border-radius-bottomright:0.5em; -webkit-border-bottom-right-radius:0.5em; /*TOFFER*/
  }
  ul#threadgroups li.selected {
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0,#52251C), color-stop(0.5,#7A3D30), color-stop(1,#52251C)) !important;
    background: -moz-linear-gradient(top, #52251C, #7A3D30, #52251C) !important;   
  }
  .thead{
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0,#52251C), color-stop(1,#7A3D30)) !important;
    background: -moz-linear-gradient(top, #52251C, #7A3D30) !important;   
    -moz-border-radius-bottomright:0.5em; -webkit-border-bottom-right-radius:0.5em;
  }
  #menu_bar ul {                              /* Menu Bar Popups */
    border:1px solid black; background: #D4C37E !important;
    background: -moz-linear-gradient(top, #D4C37E, #B5A452) !important;
    background: -webkit-gradient(linear, left top, left bottom, from(#D4C37E), to(#B5A452)) !important; /* Webkit */
  }
  #menu_bar ul li:hover {
    background: #F5EDE0 !important;             /* Menu Bar Highlighted */
  }
  .panelsurround, .vBulletin_editor, .imagebutton, .controlbar, #vB_Editor_QR_textarea { /* Forum Editor */
    background: #E2D2AE !important;              /* same as central bar bg */
  }
  .panel, #vB_Editor_001_textarea {
    background: #F5EDE0 !important;              /* same as post content */
  }
}
