css:
/* CSS Document */

@-moz-document 

url-prefix("http://www.charazay.com/")

{

body{
	margin-top:0 !important;
	Background-Color:#000000 !important;
	background-attachment:fixed !important;
}

span#time {
	color: #000000 !important; 
	text-decoration: none !important;
}


body, p, td, input, select {
	font-family: verdana !important;
	font-size: 12px !important;
	}
	
checkBox, radio, input{
	border: none !important;
}		
	
td.x_even{
background-color:#CFEEF5 !important;
}

img {
	border-style: none !important;
	}
	
hr.thin {
	clear:both !important;
	height:1px !important;
	background:#D1E1E5 !important;
	color:#D1E1E5 !important;
	margin:6px 0px 6px 0px !important;
	border:0px !important;
	padding:0px !important;
	}
	
table {
	border: 0 !important;
 	line-height: 15px !important;
	font-family: Verdana, Arial, Helvetica, sans-serif !important; 
	font-size: 12px !important;
	border-style:none !important;
	}

a:link {  color: #631F1F !important; text-decoration: none !important;}
a:visited {  color: #631F1F !important; text-decoration: none !important;}
a:hover {  color: #B5B548 !important; text-decoration: none !important;}


input { border: 1px #777777 solid !important;}	
select  { background-color: #eeeeee !important;}

table.menu a{ 
width:100% !important; 
display: block !important;
}
 
.grid_12 a {
color: #DDDDDD !important; 
text-decoration:none !important; 
line-height:15px !important; 
text-indent:25px !important;
font-family: verdana !important;
font-size: 12px !important;
}

.grid_12 a:link {
color: #DDDDDD !important; 
}

.grid_12 a:active { 
color: #DDDDDD !important;
}

.grid_12 a:visited {
color: #DDDDDD !important; 
}

.grid_12 a:hover {
color: #B5B547 !important; 
}

}
