css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gaiaonline.com") {
html, body{background:#101010!important;}

.imgAvatar{position:fixed!important; left:0px!important; bottom:55px!important;height:100px!important;top:auto!important; z-index:0!important;}

.changeMessages{position:fixed!important; bottom:200px!important; left:0!important;top:auto!important;background:#303030!important;border-radius:5px!important;}

.changeMessages *{background:transparent none!important;}

.changeMessages h3{display:none!important}

.hud-stats, .userName{position:fixed!important;z-index:999!important;}

#achievements-unlocked-list, .trophy-icon, #cta-get_gaia_cash, .gcash-icon, .hud-group-spacer, .gaiaLogo{display:none!important;}

.header_content{background:transparent none!important;height:0px!important;}

#gaia_content, #content-padding, .yui-g{background:#202020!important;z-index:999!important;}

.user-sig{height:0px!important; overflow:hidden!important;padding-top:10px!important; background:transparent url(http://trezoid.webs.com/Skins/arrow.gif)center top no-repeat!important;width:20px!important;color:#fff!important;}
.user-sig:hover{height:auto!important;background:transparent none!important;width:auto!important;}

.post-bubble{background:#303030!important;border-radius:10px!important;}
.post-bubble *{background:transparent none!important;color:#fff!important;}

.quoted{background:rgba(100,100,100,0.4)!important;border-radius:5px!important;border-color:#505050!important;}

.detail-navlinks{background:transparent none!important;}

a, .post-bubble a{color:#2B8DAF!important;}
a:hover{color:#20A4AF!important;}

.post{border-bottom:2px solid #404040!important;}

.achievement_share, #gigya{display:none!important;}

.forum_userstatus{top:180px!important;}

tr{background:rgba(100,100,100,0.4)!important}
tr tr tr tr:hover, .forum-list tr:hover{background:rgba(100,100,100,0.9)!important;}

.rowon{background:rgba(100,100,100,0.3)!important}

.subforums, .forumline, .forumline *{background:#404040!important;border-width:0!important;}

.hd div, .ft .rc_bottom_left, .ft .rc_bottom_right{display:none!important;}

.ft .forum_buttons{display:inline!important;}

.forumline .postcontent, .forumline .postcontent *{background:#202020!important;}

.forumline .postcontent{padding:20px!important;}

tr tr td img{display:none!important;}

#gaia_content{z-index:999!important;}

.statuslinks{z-index:999!important;}

#simsgenerationsforum, #screen_king{display:none!important;}
}
