#dashboard_index{background:url(http://26.media.tumblr.com/tumblr_ljqftpnL5Y1qcx8a5o1_500.gif) !important;}















#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:#000000!important;}















ol#posts li.notification{background-color: #000000 !important;







border-bottom: 1px solid #2F465C !important;







color: #000000 !important;}















