css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.transqueernation.com/"),
               url("http://www.transqueernation.com"),
               url("http://transqueernation.com/"),
               url("http://transqueernation.com"),
               url-prefix("http://www.transqueernation.com/page"),
               url-prefix("http://transqueernation.com/page"),
               url-prefix("http://www.transqueernation.com/photo"),
               url-prefix("http://transqueernation.com/photo"),
               url-prefix("http://www.transqueernation.com/video"),
               url-prefix("http://transqueernation.com/video"),
               url-prefix("http://www.transqueernation.com/blog"),
               url-prefix("http://transqueernation.com/blog"),
               url-prefix("http://www.transqueernation.com/profiles/blog"),
               url-prefix("http://transqueernation.com/profiles/blog"),
               url-prefix("http://www.transqueernation.com/profiles/members"),
               url-prefix("http://transqueernation.com/profiles/members"),
               url-prefix("http://www.transqueernation.com/profiles/message"),
               url-prefix("http://transqueernation.com/profiles/message"),
               url-prefix("http://www.transqueernation.com/profiles/settings"),
               url-prefix("http://transqueernation.com/profiles/settings"),
               url-prefix("http://www.transqueernation.com/profiles/connections"),
               url-prefix("http://transqueernation.com/profiles/connections"),
               url-prefix("http://www.transqueernation.com/profiles/profile/emailSettings"),
               url-prefix("http://transqueernation.com/profiles/profile/emailSettings"),
               url-prefix("http://www.transqueernation.com/profiles/profile/privacySettings"),
               url-prefix("http://transqueernation.com/profiles/profile/privacySettings"),
               url-prefix("http://www.transqueernation.com/profiles/profile/myPageSettings"),
               url-prefix("http://transqueernation.com/profiles/profile/myPageSettings"),
               url-prefix("http://www.transqueernation.com/forum"),
               url-prefix("http://transqueernation.com/forum"),
               url-prefix("http://www.transqueernation.com/events"),
               url-prefix("http://transqueernation.com/events"),
               url-prefix("http://www.transqueernation.com/groups"),
               url-prefix("http://transqueernation.com/groups"),
               url-prefix("http://www.transqueernation.com/main"),
               url-prefix("http://transqueernation.com/main"),
               url-prefix("http://www.transqueernation.com/opensocial"),
               url-prefix("http://transqueernation.com/opensocial"),
               url-prefix("http://www.transqueernation.com/chat"),
               url-prefix("http://transqueernation.com/chat"),
               url-prefix("http://www.transqueernation.com/friends")

{

/*-----CSS EDITING BEGINS HERE-----*/


/*-----REMOVED BACKGROUND IMAGE-----*/

body {
background-color: white !important;
background-image: none !important;
}

/*-----MADE THE NAVIGATION BAR TEXT BLACK-----*/

body, #xg_navaigation li.this a, .xg_theme-body-t {
color: black !important
}

/*-----CHANGED MOST OF THE FONT TO VERDANA-----*/

.xg_theme-body-f {
font-family: Verdana !important
}

/*-----CHANGED HEADER NUMBERS 1 & 3 TO DARK GRAY-----*/

#xg #xg_body h1 {
color: #696969 !important
}

.xg_module_body h3, .xg_module_body caption {
color: #696969 !important
}

/*-----GAVE MOST BUTTONS A WHITE BACKGROUND-----*/

.xg_theme-button-c {
background: none repeat scroll 0 0 white !important
}

/*-----MADE LINKS BLUE-----*/

a, .xg_theme-link-c {
color: blue !important
}

/*-----MADE THE HEADER BACKGROUND WHITE-----*/

#xg, .xg_headerbg {
background-color: white !important
}

/*-----MODIFIED THE MASTHEAD TO 273 PIXELS AND THE NAVIGATION BAR TO ZERO PIXELS IN THE TOP MARGIN. I did this only to satisfy a compulsion.-----*/

#xg_masthead {
height: 273px !important;
min-height: 273px !important
}

#xg_navigation {
margin-top: 0 !important
}

/*-----MADE MODULE HEADERS LEFT BORDER DEEP RED-----*/
.xg_module_head {
border-left: solid #990000 !important
}

/*-----MADE MINOR HEADERS WHITE WITH BLACK FONT-----*/

#xg_head, #xg_head a, .xg_theme-header-c {
background-color: white !important;
color: black !important
}

/*-----CENTERED THE NAVIGATION BAR-----*/

#xg_navigation ul {
float: right !important;
margin-top: 0 !important;
margin-right: 60px !important
}

/*-----MADE WIDGETS AND MODULES WHITE-----*/

.xg_module_body, .xg_theme-modulebody-bc {
background-color: white !important
}


/*-----CSS EDITING ENDS HERE-----*/

}
