css:
@-moz-document url-prefix("http://ucf.rivals.com") {
body:first-child{
 display: none;  
}

table.columns tr td{
  background-color: black !important;
  color: #e8e2d5;
}

table.columns td.header{
  border-radius: 10pt 1pt 1pt 1pt / 7pt 1pt 1pt 1pt;
}

table.columns td.header a:hover{
 color: #e8e2d5 !important;
}

iframe{
 display:none;
}

table.liquid{
 display:table;
}


table.liquid[style]{
 display:none;
}

td.GlobalMarketing{
 display:none;
}

#websearch{
  background: #000000 !important;
}
}

@-moz-document url-prefix("http://ucf.rivals.com/forum.asp") {
td.subnav08:first-child{
 border-radius: 0pt 0pt 0pt 8pt;
}
td.subnav08right{
background-color: #BA9A4C;
}

td.nav:first-child{
  border-radius: 8px 0pt 0pt 0pt;
background-image: url(data:image/png;base64,);
background-repeat: no-repeat;
padding-left: 28px !important;
background-position: 5px 4px;
}

td.nav + td.nav {
  border-radius: 0pt 8pt 0pt 0pt;
  padding: 0pt 6pt 0pt 0pt !important;
  text-align: right !important;
}

img[src="http://vmedia.rivals.com/images/database/staryellow.gif"]{
  padding: 12px 12px 0 0;
  height: 0;
  overflow:hidden;
  background-image: url(data:image/png;base64,);
}


img[src="http://vmedia.rivals.com/uploads/8/4109.gif"],img[src="http://vmedia.rivals.com/images/premium.gif"]{
padding: 12px 12px 0 0;
height: 0;
overflow:hidden;
background-image: url(data:image/png;base64,);
}

img[src="http://vmedia.rivals.com/images/mboard06_clear.gif"]{
padding: 20px 20px 0 0;
height: 0;
overflow:hidden;
background-image: url(data:image/png;base64,);
}

img[src="http://vmedia.rivals.com/images/mboard06_newicon.gif"]{
padding: 22px 22px 0 0;
height: 0;
overflow:hidden;
background-position:15px 6px;
background-image: url(data:image/png;base64,);
background-repeat:no-repeat;
}

img[src="http://vmedia.rivals.com/images/mboard06_tickericon.gif"],img[src="http://vmedia.rivals.com/images/mboard06_newsicon.gif"]{
padding: 17px 17px 0 0;
height: 0;
overflow:hidden;
background-repeat:no-repeat;
background-position:15px 0px;
background-image: url(data:image/png;base64,);
}

img[src="http://vmedia.rivals.com/images/mboard06_arrow.gif"]{
 display: none;
}

iframe{
 visibility:hidden;
}

table.liquid{
 display:table;
}


table.liquid[style]{
 display:none;
}

td.GlobalMarketing{
 display:none;
}

#websearch{
  background: #000000 !important;
}

script + table[width="100%"]{
 width:70.8em !important;
}

td[valign="top"] br{
 display: none;
}

font[color="cc0000"]:after{
  content:" "
}

div#mboardtable{
 width:100em;
}

center table{
  display: none;
}


td.fnavbg[nowrap] + td{
   width: 5em;
   text-align: left !important;
}

#mboardnav td.fnavbg{
 width: 8em;
 height: 2em;
 padding: 5px;
 border-right: white 1px dotted;
 background-color:black;
}

#mboardnav td.fnavbg a{
 padding: 0px !important;
 border: 0px;
}


td.tdtopic{
   width:  36pt !important;
   font-size: 10pt !important;
}


td.tdtopic[title] + td{
   width: 7em;
   text-align:left !important;
}



td.tdtopic  + td + td{
   width: 6em;
   text-align:left !important;
   font-size: 10pt !important;
}



td.tdtopic  + td + td + td{
   width: 6em;
   text-align:left !important;
   font-size: 10pt !important;
}


td.tdtopic + td + td + td  font{
   font-size: 8pt !important;
}



td.tdtopic + td + td + td + td{
   width: 4em;
   text-align:right !important;
   font-size: 10pt !important;
}



td.tdtopic + td + td + td + td + td{
   width: 4em;
   text-align:right !important;
   font-size: 10pt !important;
}


#mboardnav table{
 background-color: black !important;
}

#mboardnav td.nav{
 display:none;
}
}

@-moz-document url-prefix("http://ucf.rivals.com/showmsg.asp") {
td.subnav08:first-child{
 border-radius: 0pt 0pt 0pt 8pt;
}

img[src="http://vmedia.rivals.com/images/mboard06_arrow.gif"]{
 display: none;
}

iframe{
 display:none;
}

table.liquid{
 display:table;
}


table.liquid[style]{
 display:none;
}

tr>td[bgcolor="#e8e2d5"]:first-child{
  border-radius: 10px 0px 0px 10px / 10px 0px 0px 10px;
}

tr>td[bgcolor="#e8e2d5"]+td[bgcolor="#e8e2d5"]{
  border-radius: 0px 10px 10px 0px / 0px 10px 10px 0px;
}


tr+tr>td[colspan="2"]{
  background-color: white !important;
}

body > table[width="100%"] > table[width="100%"]{
  width: 740px !important;
}

body>table[width="100%"]{
 width: 10.0in !important;
}



hr+font>blockquote{
  display:block;
  max-width:550px;
  max-height:150px;
  padding-right: 7px;
  overflow:auto;
}

hr{
  color: #BA9A4C;
  background-color: #BA9A4C;
  border: none 0;
  border-top: 1px dotted  #e8e2d5;
  
  margin: 10px auto 0 0;
  height: 1px;
}

.message-body{
 padding-bottom:5px;
}

.message-ip,.message-date{
  font-size: 66% !important;
}

.message-signature{
 margin-bottom: 2px;
}




hr+font{ 
  display:block;
  max-height:450px;
  max-width:690px;
  overflow:auto;
  padding-right: 7px;
}

font[size="2"]{
 font-size: 10.5pt !important;
}


#websearch{
  background: #000000 !important;
}

#featureproduct,#sitenews{
  border-radius:8px;
  padding: 8px 0px;
}
}
