css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {

#masthead {
position: relative;
top : 0 ; 
background: #fff;
}

#masthead .user-info {
height:22px !important;
text-align:left !important;
width:550px !important;
position: relative !important;
left: -2em !important;
}

#masthead .info-section {
margin: 0 !important;
padding: 0 !important;
}
/*
#header-wrapper {
border-bottom:1px solid #CCCCCC !important;
height:25px !important;
}
*/


#otherVidsDiv {
width: 250px !important;
}

.urlField, .embedField {
width: 180px !important;
}


#masthead .logo img , #ad300x250
{
display: none !important;
}

#thisVidCell
{

padding-right: 1em !important;
padding-left: 1em !important;

}


#promotedVidsContainer, .nav , #channelBrandCap, .hpPurpleBlockBot, .hpYellowBlockBot, .leftcap, .rightcap, #footer, #copyright , #vidTitle  {
display: none !important;
}

#hpSideContent{
width: 230px !important;
}

.search-bar, .bar{
width: 350px !important;
float: right !important;
position: relative !important;
top: -1em !important;
left: -5em !important;
background: none !important;
}

#masthead #search-form {
margin: 0 !important;
padding: 0 !important;
text-align: right !important;

}

#upload-button {
 margin:0 !important;
 padding: 0 !important;
}

.action-button-leftcap,  .action-button-rightcap {
display: none !important;
}

#search-term{
  width: 10em !important;
}

#baseDiv {
background-color: #fff;
width: 790px !important;
margin: 0 !important;
padding: 0 !important;

}


}
