css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://go.infinise.com/"){
body{background:#000}
div#input input#i,form#form div#box,html body div#container form#form div#box div#input
{background:none!important}
div#input input#i{-moz-appearance:none!important;color:white!important}

#form{min-width:965px!important;height:130px;-moz-border-radius:880px/180px!important;padding-top:63px!important;opacity:.93!important;background:-moz-linear-gradient(-90deg,Hsla(0,0%,100%,0.9),rgba(0,0,0,.45)70%) !important;-moz-box-shadow:0 6px 4px 6px rgba(0,0,0,.65)!important;position:relative!important;margin-left:-240px!important;margin-right:auto!important;margin-top:-125px!important;}



#box{width:416px!important;margin-left:25%!important}
div#engines{z-index:100!important}

p#method{position:absolute;left:165px!important;top:-7px!important;}
ul#sugs{top:110px!important;background:-moz-linear-gradient(transparent,transparent)!important;border:none!important}

div#input input#i{border:none!important}
html body div#container form#form div#box div#input input#i{
-moz-appearance:none!important;
background-color:rgba(0,0,0,.3)!important;
color:white!important;		
border:none!important;
-moz-border-radius:6px/30px;
-moz-box-shadow:0 1px 1px 2px rgba(0,0,0,.5)!important;

}

form#form div#box ul#sugs li,#infoBox
{
-moz-appearance:none!important;
background:rgba(0,0,0,.7)!important;
-moz-border-radius:10px/40px! important;
border: 0.5px outset rgba(0,0,0,.7)!important;
margin-top:.5%!important}

form#form div#box ul#sugs li.active
{-moz-transition:all .5s ease-in-out;
-moz-appearance:none!important;
background:-moz-linear-gradient(-300deg,rgba(0,0,0,.9),
 rgba(0,0,0,.55))!important;
-moz-border-radius:10px/40px!important;
-Moz-transform:scale(1.2)!important;}
ul#sugs li.active>*{background:none!important}

a.active:not([id="google_logo"]):not([id="wikipedia_logo"]):not([id="youtube_logo"]):not([id="twitter_logo"]),
a[onclick="setPlace(this)"]{-moz-appearance:none!important;
-moz-transition:all .5s ease-in-out;
background:rgba(1500,400,1000,.35)!important;
opacity:.9!important;
border:3px outset rgba(0,0,0,.6)!important;
color:black!important;
-moz-border-radius:10px/15px!important;
-moz-box-shadow:inset 8px -8px 16px -8px black,
inset -8px 8px 16px -8px black,
-8px 8px 16px -8px white!important;
font-style:italic!important;
text-shadow:grey 0 .5px .5px!important;
opacity:.99!important}

a:not([id="google_logo"]):not([id="wikipedia_logo"]):not([id="youtube_logo"]):not([id="twitter_logo"]):hover{-moz-box-shadow:inset 8px -8px 16px -8px black,
inset -8px 8px 16px -8px black,
-8px 8px 16px -8px black!important;
color:white!important}
#infoBox{-moz-border-radius:10px!important}
#corner,#infoBox{margin-right:30px!important}
a#toggleInfo{margin-right:61px!important;opacity:.9!important}
html body div#container form#form div#box div#input input#i:focus
{
background:-moz-linear-gradient(-300deg,rgba(600,400,300,.3),rgba(0,0,0,.65)70%)!important}
}
@-moz-document url-prefix("http://"),url-prefix("https://") {
html,body{opacity:.99!important}}
