@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("bugguide.net") {

/* "Browse" taxonomy links */
div.node-title > a {
color: #feb900 !important;
}

p.header-menu {
white-space: nowrap !important;
}

/* Image table-alt */
td.node-main-alt {
background-color:#222222 !important;
color: #dddddd !important;
}

/* html */
html {
color: #eeeeee !important;
}

/* body */
html body {
color: #cccccc !important;
}

/* Image table */
td.node-main {
background-color:#333333 !important;
color: #dddddd !important;
}

/* Image table byline */
div.node-byline {
color: #cccccc !important;
}



/* ............ Forum ............ */




/* Forum section dark */
div#forum tr.dark td.description, div#forum tr.dark td.topics, div#forum tr.dark td.posts, div#forum tr.dark td.last-reply {
background-color: #191919 !important;
border-radius: 5px !important;
}

/* Forum section light */
div#forum tr.light td.description, div#forum tr.light td.topics, div#forum tr.light td.posts, div#forum tr.light td.last-reply {
background-color: #292929 !important;
border-radius: 5px !important;
}

/* Forum section container */
div#forum tr.dark, div#forum tr.light {
background-color: #494949 !important;
}

/* forum topic list */
div#forum table {
padding-left: 15px !important;
}

/* Forum topic dark */
div#forum table tbody tr.dark {
background-color: #494949 !important;
}

/* Topic sections dark (icon, title, replies, etc.) */
div#forum table tbody tr.dark td.icon, div#forum table tbody tr.dark td.topic, div#forum table tbody tr.dark td.replies, div#forum table tbody tr.dark td.created {
background-color: #191919 !important;
border-radius: 5px !important;
}

/* Forum topic title link */
div#forum table tbody tr.dark td.replies, div#forum table tbody tr.dark td.topic a, div#forum table tbody tr.light td.topic a {
color: #dddddd !important;
}

/* Forum topic title link:hover */
div#forum table tbody tr.dark td.replies, div#forum table tbody tr.dark td.topic a:hover, div#forum table tbody tr.dark td.replies, div#forum table tbody tr.light td.topic a:hover {
color: #ffffff !important;
}

/* Topic sections light (icon, title, replies, etc.) */
div#forum table tbody tr.light td.icon, div#forum table tbody tr.light td.topic, div#forum table tbody tr.light td.replies, div#forum table tbody tr.light td.created {
background-color: #292929 !important;
border-radius: 5px !important;
}

/* Sort by type */
div#forum table tbody tr th {
padding-bottom: 5px !important;
text-align: center !important;
border: 0px solid #000000 !important;
}

/* Forum pager container */
div#forum table tbody tr.light td.pager {
padding-top: 10px !important;
padding-bottom: 10px !important;
}







/* ............ Main content ............ */









/* Main page */
html body.body {
background-color: #222222 !important;
}

/* Ultimate container */
html body.body div.container {
border:none !important;
background-color: #222222 !important;
}

/* Misc box header */
div.box-header {
background-color: #494949 !important;
border-radius: 5px 5px 0px 0px !important;
color: #feb900 !important;
box-shadow: 4px 4px 4px 0px #111111 !important;
-webkit-box-shadow: 4px 4px 4px 0px #111111 !important;
-moz-box-shadow: 4px 4px 4px 0px #111111 !important;
}

/* Misc box body */
div.box-body {
background-color: #494949 !important;
box-shadow: 4px 4px 4px 0px #111111 !important;
-webkit-box-shadow: 4px 4px 4px 0px #111111 !important;
-moz-box-shadow: 4px 4px 4px 0px #111111 !important;
border-radius: 0px 0px 5px 5px !important;
color: #dddddd !important;
}

/* Misc column 01 */
td.col1 {
background-color: #222222 !important;
}

/* Misc column 02 */
td.col2 {
background-color: #222222 !important;
}

/* Main content container */
td.node-container {
background-color: #222222 !important;
color: #cccccc !important;
border-radius: 10px !important;
border: 2px solid #333333 !important;
padding: 15px !important;
}

/* Image thumbnail */
html body.body div.container table tbody tr td.col2 table tbody tr td.col2-body a img.bgimage-thumb {
/*margin: 0px 0px 10px 0px !important;*/
}

/* Content separator div */
div.bgseparator {
background-color: #333333 !important;
border: 1px solid #444444 !important;
border-radius: 2px !important;
}

/* Main content container buffer table */
td.col2-body {
background-color: #333333 !important;
border:none !important;
border-radius: 15px !important;
box-shadow: 4px 4px 6px 0px #111111 !important;
-webkit-box-shadow: 4px 4px 6px 0px #111111 !important;
-moz-box-shadow: 4px 4px 6px 0px #111111 !important;
padding: 15px 15px 0px 15px !important;
color: #eeeeee !important;
}

/* Data table dark */
tr.bgdata-row td {
background-color: #292929 !important;
border-style:solid !important;
border-color:#111111 !important;
border-width: 1px 1px 1px 1px !important;
width: 23px !important;
height: 23px !important;
}

/* Data table light */
tr.bgdata-row-alt td {
background-color: #373737 !important;
border-style:solid !important;
border-color:#111111 !important;
border-width: 0px 1px 0px 1px !important;
width: 23px !important;
height: 23px !important;
}



/* Data pixel image replacer */
td.bgdata-data-column img[src="themes/bugguide/images/pixel.gif"] { background:transparent url(data:image/gif;base64,) no-repeat !important; padding:0px 0px 15px 15px !important; height:0px !important; width:0px !important; border: 0px !important; }





/* ............ Links ............ */






/* Main links */
a:link {color:#FFFFFF !important;
} 

a:visited {color:#FFFFFF !important;
}

a:hover {color:#bbbbbb !important;
}

a:active {color:#FFFFFF !important;
}






/* ............ Comments ............ */






/* Comment container */
div.comment {
background-color: #202020 !important;
color: #dddddd !important;
border-radius: 10px !important;
box-shadow: 4px 4px 6px 0px #111111 !important;
-webkit-box-shadow: 4px 4px 6px 0px #111111 !important;
-moz-box-shadow: 4px 4px 6px 0px #111111 !important;
-moz-animation-name: wobble;
-moz-animation-duration: 5s;
}


/* Comment subject */
div.comment-subject {
background-color: #6f6f6f !important;
color: #000000 !important;
padding-left: 10px !important;
border-radius: 10px !important;
border: 1px solid #333333 !important;
}

/* Comment body */
div.comment-body {
padding-left: 10px !important;
}

/* Comment byline */
td.comment-byline {
padding-left: 5px !important;
}





/* ............ Footer/header ............ */





/* Footer */
div.footer {
background-color: #333333 !important;
box-shadow: 4px 4px 6px 0px #111111 !important;
-webkit-box-shadow: 4px 4px 6px 0px #111111 !important;
-moz-box-shadow: 4px 4px 6px 0px #111111 !important;
border-radius: 5px 5px 5px 5px !important;
color: #dddddd !important;
margin: 30px 0px 0px 0px !important;
}

/* Main header */
div.header {
background-color: #222222 !important;
color: #9f9f9f !important;
}

/* Main logo */
img[src="/themes/bugguide/images/bglogo.gif"] { background:transparent url(data:image/gif;base64,)
no-repeat !important; 
padding:0px 0px 48px 161px !important; 
height:0px !important; 
width:0px !important; 
border: 0px !important; 
}





/* ............ Breadcrumbs/nav buttons and related elements ............ */





/* Breadcrumbs */
div.bgpage-roots a, html body.body div.container table tbody tr td.col2 table tbody tr td.col2-body div.breadcrumb a {
font-weight:bold !important;
color: #feb900 !important;
}

/* Breadcrumbs hover */
div.bgpage-roots a:hover, html body.body div.container table tbody tr td.col2 table tbody tr td.col2-body div.breadcrumb a:hover {
font-weight:bold !important;
color: #ffe400 !important;
}

/* Menu bar container */
div.menubar-container {
background-color: #222222 !important;

}

/* Menu bar */
td.menubar {
background-color: #222222 !important;
}

/* Guide menubar */
td.guide-menubar {
background-color: #222222 !important;
border: 0px solid #000000 !important;
padding-left: 20px !important;
}

/* Guide menubar selected */
div.guide-menubar-selected {
background-color: #333333 !important;
color: #ff6600 !important;
height: 15px !important;
border-radius: 8px 8px 0px 0px !important;
border: 0px #000000 !important;
border-style:solid solid none solid !important;
margin: 0px 1px 0px 1px !important;
text-shadow: 3px 3px 3px #111111 !important;
}

/*Guide menubar item */
td.guide-menubar a {
background-color:#333333 !important;
height: 15px !important;
border-radius: 8px 8px 0px 0px !important;
margin: 0px 2px 0px 2px !important;
}

/* Guide menubar item visited */
td.guide-menubar a:visited {
background-color:#333333 !important;
}

/* Guide menubar item hover */
td.guide-menubar a:hover {
background-color:#333333 !important;
color: #ff6600 !important;
}

/* Guide menubar item active */
td.guide-menubar a:active {
background-color:#000000 !important;
}

/* Breaks between nav buttons */
span {display:none !important;}

/* Unbreaks taxonomy page */
.bgpage-taxon-title, .bgpage-taxon-desc {display:inline !important;
color: #eeeeee !important;}

/* Taxonomy links */
.bgpage-taxon-title a, .bgpage-taxon-desc a { color:#feb900 !important;}

/* Taxonomy links hover */
.bgpage-taxon-title a:hover, .bgpage-taxon-desc a:hover { color:#ffe400 !important;}

/* Menubar selected */
div.menubar-selected {
background-color: #222222 !important;
text-shadow: 3px 3px 3px #000000 !important;
color:#ff6600 !important;
/*border: 1px solid #666666 !important;
border-radius: 9px !important;
padding-bottom: 4px !important;
padding-top: 0px !important;
box-shadow: 2px 2px 5px 0px #000000 !important;*/
}

/* Menubar item */
td.menubar a {
background-color:#222222 !important;
}

/* Menubar item visited */
td.menubar a:visited {
background-color:#222222 !important;
}

/* Menubar item hover */
td.menubar a:hover {
background-color:#222222 !important;
color: #ff6600 !important;
}

/* Menubar item active */
td.menubar a:active {
background-color:#000000 !important;
}


