img[src="themes/bugguide/images/last.gif"] { background:transparent url(data:image/gif;base64,R0lGODlhDwAPAMQAAAMDAg0NChsaFHRvWGFdSh8dFXhyWRQTD+bbruPYrOHWqtbMorWtia2lg6WdfWxnUlxYRkxIOZ2VdzEuJVJQSkpJRgoJB05NSwUEAwcGBQgHBgAAAP///wAAAAAAAAAAACH5BAEAABwALAAAAAAPAA8AAAVTILdxZGlu6Gah5omgg3Ow7fZuAYLINGmPG4ZuNxuJXiLCUMdDITeaxJI5+fka06HhCVhkF4/CMzIFY5xAydDM+m0yCgTbeERB5rWb4Ew/tfstJSEAOw==) no-repeat !important; padding:0px 0px 15px 15px !important; height:0px !important; width:0px !important; border: 0px !important; } 

